INSERT INTO `openletters` (`letterID`, `title`, `content`, `slug`, `contributor`, `targetName`, `targetDetails`, `headline`, `language`, `timestamp`, `visits_count`) VALUES
(1, 'Pengiriman SMS Promo Makanan Cepat Saji dari Telkomsel Cukup Mengganggu', '<p>Ini adalah contoh surat terbuka yang ditujukan pada Telkomsel terkait pengiriman SMS sepihak untuk mempromosikan makanan cepat saji tanpa persetujuan penerima. Mengendarai vespa bukan main, Judika sahabat Urip nekat pergi. Merkurius venus bumi mars, jupiter saturnus uranus neptunus pluto.</p><p>Ingsun amatak ajiku si jaran goyang. Tak goyang tengahing latar. Gegamane cumethi, sodo lanang saking swarga. Sun sabetake gunung, gugruk. Sun sabetake watu, bengkah. Sun sabetake segoro, asat. Sun sabetake atine si jabang bayi anu bin/ti anu, teko welas teko asih. Bla bla bla bla bla bla...</p>', 'pengiriman-sms-promo-makanan-cepat-saji-dari-telkomsel-cukup-mengganggu', 1, 'Telkomsel', 'Telkomsel adalah perusahaan seluler di Indonesia', 'N', 'english', '1468338568', 80),
(2, 'Keadaan Selokan Sekolah ketika Hujan Deras', '<p>Assalamualaikum, wr wb.<br>Yth. Bapak Kepala Sekolah SMPN 23 Palangkaraya<br><br>Musim hujan memang sangat mengganggu untuk beberapa hal. Hal ini saya rasakan ketika sedang ikut melaksanakan kegiatan belajar mengajar di kelas, khususnya kelas 94. pada saat hujan deras memang meresahkan karena saluran-saluran pembuang tidak berfungsi dengan semestinya hal ini menyebabkan banjir di selokan-selokan sekolah sehingga saya dan teman-teman susah untuk melewati selokan yang tergenang sampai ke jalanan apabila kami ingin pergi untuk jajan di kantin.<br><br>Selokan yang penuh tergenangi oleh air hingga keluar ke lapangan sekolah mengakibatkan sepatu kami basah terendam oleh air, yang membuat lantai-lantai sekolah menjadi kotor karena terkena air yang bercampur dengan tanah. </p><p>Hal ini juga menjadikan kelas menjadi kotor. Salah satu factor penyebab terjadinya banjir di selokan-selokan sekolah adalah kurang sadarnya para penghuni sekolah khususnya siswa-siswi akan pentingnya membuang sampah pada tempatnya, sehingga kebanyakan dari mereka membuang sampah sembarangan seperti membuang sampah ke selokan sekolah. Hal ini merupakan perbuatan melanggar peraturan sekolah.<br><br>Sebelumnya, saya sangat berterima kasih kepada bapak Kepala Sekolah yang telah membuat SMPN 23 Palangkaraya menjadi bersih dan asri. Banyaknya pohon-pohon menambah pasokan oksigen dan mempercantik sekolah, fungsi pepohonan itu juga sebagai penampung air ketika hujan deras mengguyur, tetapi itu tidak cukup untuk menahan air hujan yang mengalir karna rendahnya dataran sekolah dibanding jalanan depan sekolah. </p><p>Yang pertama, sekiranya bapak Kepala Sekolah berkenan untuk segera membantu membersihkan dan melancarkan saluran pembuangan agar tidak tersendat dan tidak mengakibatkan genangan air yang ada di selokan. </p><p>Lalu membuat dan mempertegas peraturan-peraturan sekolah agar siswa-siswi tidak lagi melanggar peraturan tersebut. Yang terakhir, untuk bapak Kepala Sekolah saya mengingatkan untuk selalu memberikan amanat kepada seluruh siswa untuk menjaga lingkungan dan memberikan kesadaran akan bahayanya membuang sampah tidak pada tempatnya.</p><p>Demikian sengaja surat ini saya sampaikan melalui redaksi surat terbuka yang ada di <a href="http://www.dwitri.com">www.dwitri.com </a> dengan harapan agar segera mendapat tanggapan, mohon maaf jika bapak tidak berkenan, terima kasih.<br><br>Wassalamualaikum, wr wb. <br>An Nama Murid2 SMPN 23 Palangkaraya</p>', 'keadaan-selokan-sekolah-ketika-hujan-deras', 10, 'Kepada Yth. Bapak Kepala Sekolah SMPN 23 Palangkaraya', 'di Tempat', 'N', 'indonesia', '1468870230', 79),
(3, 'Surat Terbuka Untuk Angan (Edited)', '<p>Anggan.................................................................................................................................</p><p>,,,,,,,,,,,,,,,,,,,,,,,,,,,,,,,,,,,,,,,,,,,,,,,,,,,,,,,,,,,,,,,,,,,,,,,,,,,,,,,,,,,,,,,,,,,,,,,,,,,,,,,,,,,,,,,,,,,,,,,,,,,,,,,,,,,,,,,,,,,,,,,</p>', 'surat-terbuka-untuk-angan-edited-', 10, 'Kepada Anggan', 'di Dalam Hati', 'N', 'english', '1468340503', 114);
