INSERT INTO `posts` (`postID`, `contributor`, `postSlug`, `postTitle`, `postExcerpt`, `tags`, `postContent`, `categoryID`, `postHeadline`, `language`, `timestamp`, `visits_count`) VALUES
(1, 1, 'minus-cabaye-hazard-anggap-palace-tetap-berbahaya-january-2016', 'Minus Cabaye, Hazard anggap Palace tetap berbahaya', 'Merdeka.com - Chelsea akan memulai tahun 2016 dengan menantang tuan rumah Crystal Palace (03/01). Saat menjamu The Blues, Palace tidak akan diperkuat pemain andalannya Yohan Cabaye akibat skorsing.Kehilangan Cabaye tentu saja merupakan kabar bagus untuk ...', '', '<p><img src="http://www.dwitri.com/uploads/posts/38a4f10b4e32a9a56f26f165ea059007.jpg" alt=""></p><p><strong></strong></p><p><strong>Merdeka.com - </strong>Chelsea akan memulai tahun 2016 dengan menantang \r\ntuan rumah Crystal Palace (03/01). Saat menjamu The Blues, Palace tidak \r\nakan diperkuat pemain andalannya Yohan Cabaye akibat skorsing.<br><br>Kehilangan\r\n Cabaye tentu saja merupakan kabar bagus untuk pasukan Guus Hiddink. \r\nNamun Eden Hazard mengingatkan bahwa Palace tetap tim yang berbahaya \r\nmeski tanpa diperkuat pemain asal Prancis tersebut.<br><br>"Dia pemain \r\nyang fantastis. Dia bisa mengontrol lini tengah sendirian, dia bisa \r\nmengambil tendangan bebas dan dia pemberi umpan yang baik," ujar Hazard \r\ndi situs resmi Chelsea.<br><br>"Dia benar-benar bisa membuat perbedaan. \r\nSangat disayangkan bagi mereka Cabaye tidak bermain tetapi sangat bagus \r\nbuat kami. Mereka bukan hanya Cabaye, tapi memiliki banyak kualitas."<br><br>Hazard sendiri pernah bermain satu tim dengan Cabaye saat keduanya masih sama-sama memperkuat klub Ligue 1 Lille.(cfc/ada)</p>', '["9"]', 'Y', 'indonesia', 1454121899, 213),
(2, 1, 'bali-dinobatkan-jadi-pulau-terbaik-di-asia-dan-peringkat-2-dunia', 'Bali dinobatkan jadi pulau terbaik di Asia dan peringkat 2 dunia', 'Merdeka.com - Menteri Pariwisata (Menpar) Arief Yahya mengaku \r\nbangga Bali terpilih sebagai salah satu pulau terbaik dunia. Penobatan \r\nini berdasarkan survei yang dilakukan majalah travel ternama asal \r\nAmerika Serikat (AS) Travel+Leisure pada akhir 2015."Ba...', 'bali,pariwisata', '<p><img src="http://www.dwitri.com/uploads/posts/641bf89ccc1f312d48407b56f9c6f780.jpg"></p><p><strong>Merdeka.com - </strong>Menteri Pariwisata (Menpar) Arief Yahya mengaku \nbangga Bali terpilih sebagai salah satu pulau terbaik dunia. Penobatan \nini berdasarkan survei yang dilakukan majalah travel ternama asal \nAmerika Serikat (AS) Travel+Leisure pada akhir 2015.<br><br>"Bangga sekali Bali terpilih sebagai salah satu pulau terbaik di dunia," kata Menteri Arief Yahya di<strong> #Jakarta</strong><strong></strong>, seperti dilansir Antara, Minggu (3/1).<br><br>Berdasarkan\n Majalah Travel+Leisure, yang berpusat New York City, urutan peringkat \npulau terbaik di dunia adalah Galpagos Islands, Equador (nilai: 90,82), \nBali, Indonesia (nilai: 88,98), Maldives (nilai: 88,53), Tasmania, \nAustralia (nilai: 88,32), Santorini, Yunani (nilai: 87,93).<br><br>Sementara\n itu, urutan peringkat pulau terbaik di Asia versi majalah itu adalah \nBali, Indonesia (nilai: 88,98), Maldives (nilai: 88,53), dan Phuket, \nThailand (nilai: 79,22).<br><br>Ke depan, Menteri Arief menyatakan akan \nmempromosikan destinasi-destinasi wisata lain di Indonesia yang tak \nkalah cantiknya dengan Bali.<br><br>"Kita harus yakin potensi wisata \nkita sangat hebat, untuk destinasi snorkling, keindahan wisata selam \nbahkan Galapagos, yang terkenal itu, sudah berhasil kita kalahkan. Tidak\n tanggung-tanggung bahkan oleh dua destinasi kita yakni Rajaampat dan \nPulau Komodo," katanya.<br><br>Travel + Leisure merupakan majalah travel\n bulanan yang berbasis di New York dengan 4,8 juta pembaca diterbitkan \noleh Time Inc. Majalah itu merupakan kompetitor terberat Cond Nast \nTraveler dan National Geographic Traveler.</p>', '["5"]', 'N', 'indonesia', 1451798942, 21),
(3, 1, 'jk-pulang-ke-jakarta-naik-kereta-warga-bilang-stasiun-lebih-bersih', 'JK pulang ke Jakarta naik kereta, warga bilang stasiun lebih bersih', 'Merdeka.com - Rombongan Wakil Presiden Jusuf Kalla (JK) bersama \r\nistri, Mufidah menghabiskan waktu liburan di Yogyakarta sejak tanggal 31\r\n Desember 2015 hingga 3 Januari 2016. Ketika hendak kembali dari \r\nliburan, JK bersama rombongan pilih transportasi Kere...', 'krl,wapres', '<p><img src="http://www.dwitri.com/uploads/posts/4867487b871a5b8a8f65f81d4dc45263.jpg"></p><p><strong>Merdeka.com - </strong>Rombongan Wakil Presiden Jusuf Kalla (JK) bersama \nistri, Mufidah menghabiskan waktu liburan di Yogyakarta sejak tanggal 31\n Desember 2015 hingga 3 Januari 2016. Ketika hendak kembali dari \nliburan, JK bersama rombongan pilih transportasi Kereta Api Luar Biasa \n(KLB) yang biasa berfungsi sebagai kereta wisata.<br><br>Pengamanan lebih ketat diberlakukan di Stasiun Tugu, Yogyakarta, tempat Wapres dan rombongan bertolak menuju  <strong>#Jakarta</strong>.<br><br>Perlakuan\n berbeda di Stasiun Tugu Yogyakarta dirasakan oleh sejumlah warga \nYogyakarta yang sehari-hari beraktivitas di sekitar stasiun.<br><br>Salah\n satunya adalah petugas kebersihan, Wachyu. Wanita yang baru bekerja \nsebagai petugas kebersihan selama lima bulan di Stasiun Tugu ini mengaku\n sudah mengetahui rencana keberangkatan Wapres beserta rombongan dari \nStasiun Tugu.<br><br>"Iya (Sudah tahu Wapres pulang ke Jakarta dari \nStasiun Tugu). Kemarin sudah dikasih tahu," kata Wachyu kepada \nmerdeka.com, Minggu (3/1).<br><br>Wachyu mengaku dirinya sudah mendapat \ninstruksi lebih ketat dibanding biasanya. "Saya sudah di sini dari jam 6\n pagi. Ya suruh bersih-bersih. Saya berdua dengan teman, sekarang dia \nlagi ambil tissue (toilet tissue)," ujarnya.<br><br>Perbedaan perlakuan \ndi Stasiun Tugu juga dirasakan oleh penjual oleh-oleh yang sudah 16 \ntahun menggantungkan kondisi ekonominya dari aktivitas perjalanan di \nStasiun Tugu.<br><br>Rusti penjual Salak Pondoh di stasiun pusat Kota \nYogyakarta itu mengaku senang dengan keputusan Wapres menggunakan jasa \nkereta api dari Stasiun Tugu.<br><br>"Seneng, dari tadi saya cariin, mana (Wapres)? Jadi bisa lihat. Tapi jualan biasa saja (tidak ada perubahan)," katanya.<br><br>Rusti\n melihat kondisi Stasiun Tugu lebih bersih dibanding hari-hari biasa. \nSelain itu juga pengamanan ekstra ketat dari Paspampres hingga petugas \npengamanan Stasiun Tugu.<br><br>"Tempatnya agak lain, pengawasannya jadi lebih ketat. Lebih bersih sekarang," tutur Rusti.<br><br>Menurutnya,\n jarang sekali pejabat negara, terutama Presiden dan Wakil Presiden yang\n memanfaatkan moda transportasi kereta api dari dan ke Yogyakarta.<br><br>"Dulu Pak SBY pernah tapi pas saya enggak jualan. Kalau Wapres, baru hari ini," imbuhnya.<br><br>Rangkaian kereta rombongan Wapres JK berangkat menuju Jakarta sekitar pukul 09.30 WIB.</p>', '["3","7"]', 'N', 'indonesia', 1451799080, 29),
(4, 1, 'gara-gara-tunggakan-listrik-pbb-kubu-agung-dan-ical-memanas-lagi', 'Gara-gara tunggakan listrik & PBB, kubu Agung dan Ical memanas lagi', 'Merdeka.com - Sejak Partai Golkar pecah menjadi dua kubu, yaitu \r\nkubu Agung Laksono dan Aburizal Bakrie (Ical), perselisihan di antara \r\nkeduanya terus bergulir. Perselisihan itu terjadi juga ke masalah kantor\r\n DPP Golkar di Slipi,  #Jakarta Barat, yang berh...', 'golkar', '<p><strong>Merdeka.com - </strong>Sejak Partai Golkar pecah menjadi dua kubu, yaitu \r\nkubu Agung Laksono dan Aburizal Bakrie (Ical), perselisihan di antara \r\nkeduanya terus bergulir. Perselisihan itu terjadi juga ke masalah kantor\r\n DPP Golkar di Slipi,  <strong><a href="http://www.dwitri.com/search/Jakarta">#Jakarta</a></strong> Barat, yang berhasil dikuasai oleh Ketua Umum versi Munas Ancol, Agung Laksono.</p><p><a href="http://www.dwitri.com/uploads/posts/5b91bb424d3ba4bd4c6ec3f9ab9ebaa5.jpg" target="_blank"><img src="http://www.dwitri.com/uploads/posts/5b91bb424d3ba4bd4c6ec3f9ab9ebaa5.jpg"></a></p><p>Kubu Agung cs menduduki kantor yang beralamat di Jl Anggrek Neli \r\nMurni itu selama satu tahun. Persoalan ini pun menjadi mengejutkan \r\nsetelah terungkap bahwa selama diduduki kubu Agung, kantor tersebut \r\nmemiliki banyak tunggakan terkait pembayaran listrik dan pajak bumi dan \r\nbangunan (PBB). <br><br>Hal tersebut membuat kekisruhan di antara kubu \r\nAgung dan Ical kembali memanas. Pihak Ical menuding Agung tidak \r\nbertanggung jawab atas kewajibannya itu.</p><p>Mereka menilai Agung sudah kelewat batas sampai memiliki tunggakan \r\nyang sangat besar. Padahal selama setahun ini kubu Agung aktif melakukan\r\n kegiatan di kantor tersebut.<br><br>"Yang paling memalukan adalah \r\ntunggakan PBB yang tidak mereka bayar Rp 1 miliar lebih. Demikian juga \r\ngaji karyawan dan satpam, saya dengar juga tidak dibayar," kata \r\nBendahara Umum DPP Partai Golkar Kubu Aburizal Bakrie, Bambang Soesatyo \r\nsaat dihubungi, Jakarta, Sabtu (2/1).<br><br>Politikus Golkar Yorrys Roweyai menyebut seharusnya biaya operasional Kantor Golkar ditanggung oleh Agung Laksono.<br><br>"Iya\r\n memang betul belum bayar. Seharusnya Pak Agung yang bayar, Pak Agung \r\nyang tanggung jawab karena dia yang kuasain (Kantor Golkar)" kata Yorrys\r\n saat dihubungi.<br><br>Dengan adanya masalah tunggakan tersebut, nama \r\nGolkar kembali tercoreng. Beberapa politikus partai berlambang pohon \r\nberingin itu meminta Agung cs segera melunasi semua tagihan listrik dan \r\nPBB. "Grupnya Pak Agung ya kudu (harus) bayar atuh," kata politikus \r\nGolkar Popong Otje Djundjunan.<br><br>Menanggapi hal tersebut, Ketua DPP\r\n Partai Golkar kubu Agung Laksono, Lawrence Siburian mengaku tidak \r\nkeberatan jika tagihan tersebut harus dibebankan kepada pihaknya. Asal \r\nIcal cs tidak lagi mendatangi kantor tersebut.<br><br>"Ya tidak masalah \r\napabila kami yang membayar. Asalkan kubu Ical tidak boleh datang ke sana\r\n (Kantor DPP Partai Golkar). Kan kemarin-kemarin kantor digunakan oleh \r\ndua pihak," kata Lawrence saat dihubungi, Sabtu (2/1). <br><br>Satpam \r\nyang berjaga di Kantor DPP Partai Golkar juga membenarkan bahwa Kantor \r\nakan gelap gulita ketika malam datang. "Sudah beberapa hari mati," \r\nucapnya.</p>', '["3","4","7"]', 'Y', 'indonesia', 1451821986, 41),
(6, 10, 'wajib-dibaca-3-alasan-kenapa-kucing-mendatangi-rumah-anda', 'Wajib Dibaca, 3 Alasan Kenapa Kucing Mendatangi Rumah Anda', 'Kucing adalah fauna yang sangat familier, dia seakan selalu ada di \r\nsekitar dan dekat dengan kehidupan kita, satu pertanyaannya pernahkah \r\nanda merasa tidak terganggu dengan adanya kucing liar yang datang \r\nmendekati kita? ?Seringkali kucing-kucing liar masu...', 'kucing', '<p><img src="http://www.dwitri.com/uploads/posts/9530e15afef1e7d904d38037423734ea.jpg" alt=""></p><p>Kucing adalah fauna yang sangat familier, dia seakan selalu ada di \nsekitar dan dekat dengan kehidupan kita, satu pertanyaannya pernahkah \nanda merasa tidak terganggu dengan adanya kucing liar yang datang \nmendekati kita? ?</p><p>Seringkali kucing-kucing liar masuk ke dalam rumah dan mendekati \nkita, terutama saat kita sedang membawa makanan. Mereka mengeong seperti\n mengatakan bahwa mereka ingin meminta makanan dari kita.</p><p>Mungkin bagi para pecinta kucing, mereka tidak akan keberatan untuk \nmemberikan sedikit dari makanan yang dimiliki untuk kucing tersebut. \ntapi, bagi orang yang tidak menyukai kucing, mungkin mereka akan \nmenendang kucing tersebut atau melemparinya dengan sandal.</p><p>Ketahuilah, bahwa kucing tersebut datang kepada kita bukan tanpa \nmaksud dan sebab. Berikut 3 sebab kucing datang kepada kita, dan saat \nmengetahuinya kita akan merasa sangat merugi jika tak memberi makan pada\n kucing tersebut.</p><p><strong>Pertama</strong></p><p>Allah sedang beritahu makanan kita, hak kita itu bukan semuanya \nrezeki kita. Sebahagiannya rezeki kucing itu. Sebab itu ia datang pada \nkita.</p><p><strong>Kedua</strong></p><p>Allah sedang beritahu apabila kita memberi makanan kepada kucing itu,\n rezeki lain akan Allah ganti yang lebih baik. Sebab 1 kebaikan itu \nAllah balas 10 kebaikan. Allah maha mampu memberi lebih dari itu.</p><p><strong>Ketiga</strong></p><p>Allah sedang bagi tahu apabila kita tak memberi makanan kepada kucing\n itu, sebenarnya kita sedang menolak rezeki baru yang Allah berikan \nkepada kita. Rezeki itu luas bukan hanya pada uang. Tetapi meliputi \nsemua kehidupan. Ingin ikhlas memberi, berilah pada hewan karena hewan \ntak pandai mengucapkan terima kasih kepada kita. Ia hanya tahu minta dan\n makan.</p><p>Nah, setelah membaca tulisan ini apakah pandangan anda tentang kucing\n menjadi berubah, sukakah, ikhlaslah jika anda membagikan sedikit rejeki\n yang anda miliki? Ingat Allah akan membalasnya 10 x lipat dari apa yang\n anda beri Mau??..</p>', '["1","10"]', 'Y', 'indonesia', 1451808871, 79),
(7, 10, 'pekerjaan-yang-menyenangkan-santai-dapat-uang-apa-', 'Pekerjaan yang menyenangkan, santai dapat uang, apa?', 'Kalau bicara soal pekerjaan sudah jelas yang namanya pekerjaan pasti \r\nngga ada yang menyenangkan, yah, judulnya saja kerja, bekerja pasti \r\nbutuh tenaga, pikiran, konsentrasi dan ketrampilan. Oya satu lagi \r\ntanggung jawab, tanpa itu semua jangan harap kita b...', 'uang,pekerjaan', '<p><img src="http://www.dwitri.com/uploads/posts/48d406f0818f4de589ea55ceddab89f2.jpg"></p><p>Kalau bicara soal pekerjaan sudah jelas yang namanya pekerjaan pasti \nngga ada yang menyenangkan, yah, judulnya saja kerja, bekerja pasti \nbutuh tenaga, pikiran, konsentrasi dan ketrampilan. Oya satu lagi \ntanggung jawab, tanpa itu semua jangan harap kita bisa mengais rejeki \ndari sana. Walau ada juga pekerjaan yang tidak menghasilkan uang, \ncontohnya para pekerja sosial, para ibu mereka terkenal sebagai pekerja \nyang tidak mengharapakan imbalan apapun. Namun tetap saja bila kita \nbekerja tentu  saja akan memperoleh satu hasil, walaupun gak selalu \nberupa meteri. Betul tidak?</p><p>Oke back to topik, kira-kira apa ya pekerjaan yang menyenangkan, \nsante tapi dapat uang? Apa yaa? Hmm sebetulnya banyak lohh, tapi kitanya\n saja yang kurang jeli membidiknya. Pertama kita bahas dulu apa itu \npekerjaan yang menyenangkan, pekerjaan yang menyenangkan adalah \npekerjaan yang berhubungan dengan hobi, minat dan yang sesuai dengan \nkemampuan kita.</p><p>Sekarang mari tengok ke dalam diri sendiri, tanya dan ingat-ingat \nlagi.Apa yang paling kita minati, apa hobi kita  dan seberapa jauh \nketrampilan atau kemampuan yang kita miliki. Misalnya saja, kamu hobi \nmasak, menyanyi, menari, menulis, olah raga, dan seterusnya. Kemudian \nukur seberapa jauh kemampuan kamu pada hal-hal yang saya sebutkan tadi.</p><p>Kalau sudah yakin seberapa besar kemampuan kamu, tinggal pikirin \nbagaimana mengolah kemampuan, hobi dan minat kamu itu biar bisa \nmenghasilkan sesuatu yang berarti, yang bisa jadi sumber rejeki dan \nmensejahterakan kehidupan kamu. Sekarang coba deh kamu bayangin \nkira-kira pekerjaan apa yang bisa bikin kamu happy dan engga merasa \nterbebani malahan enjoy buat melakoninya?</p><p>Kalau saya sii.. banyak antara lain jalan-jalan terus liat-liat rumah\n yang mau di jual, saya jepret, menghubungi si pemilik rumah kemudian \nbertanya, pak, bu, boleh saya bantu memasarkan rumah bapak/ibu? Jika \nmereka setuju saya pasang iklan deh di situs onlen gratis. Selajutnya \ntinggal duduk manis di rumah, menunggu dering telp. Ya.. ada yang bisa \nsaya bantu? :D (Ini hanya sekedar contoh saja ya)</p>', '["1","5","10"]', 'N', 'indonesia', 1451800027, 30),
(8, 1, 'mengenang-sekali-lagi-sony-ericsson-t290i-sahabat-pembuat-perubahan-', 'Mengenang Sekali Lagi, Sony Ericsson T290i. Sahabat Pembuat Perubahan.', 'Ya, seperti judulnya. Bagaimana sebuah handphone Sony Ericsson dengan type T290i dapat mengubah jalan hidup seseorang. Saya adalah orang itu. Baca cerita ini untuk mendapatkan inspirasi.Pertengahan tahun 2005 saya adalah seorang kuli bangunan yang hanya tahu p...', 'sony ericsson, t290i', '<p><a href="http://www.dwitri.com/uploads/posts/98d7ec7bf11483ee28812ade0cc45ec5.jpg" target="_blank"><img src="http://www.dwitri.com/uploads/posts/98d7ec7bf11483ee28812ade0cc45ec5.jpg"></a></p><p>Ya, seperti judulnya. Bagaimana sebuah handphone Sony Ericsson dengan type T290i dapat mengubah jalan hidup seseorang. Saya adalah orang itu. Baca cerita ini untuk mendapatkan inspirasi.</p><p>Pertengahan tahun 2005 saya adalah seorang kuli bangunan yang hanya tahu pagi untuk bekerja, malam untuk tidur. Saya membeli sebuah handphone Sony Ericsson T290i ini atas inisiatif Mbak Yu saya untuk sekedar berkomunikasi. Dikarenakan harga handphone pada saat itu sangat mahal, saya membeli Sony Ericsson type T290i ini. Itupun dengan pertimbangan yang sangat panjang.</p><p>Saya sering membaca rubrik pengulas berbagai fitur dan harga ponsel. Dengan kemampuan budget yang harus saya keluarkan, saya cukup tertarik dengan ponsel Sony Ericsson ini. Tanpa pemutar musik atau kamera, yang penting saya mempunyai ponsel. Itu intinya pada waktu itu.</p><p>Waktu itu hari Sabtu, bertepatan dengan pembagian uang makan sebagai kuli bangunan saya menyiapkan waktu yang sudah lama ditunggu-tunggu. Ditambah pada hari itu saya mendapatkan kiriman uang dari Mbak Yu yang kerja di luar negeri. Dengan semangat yang luar biasa, sorenya saya jalan ke pusat perbelanjaan gadget di ITC Roxy Mas Grogol. Saya diantar dengan teman sekerja saya untuk membantu memilih produk yang bagus. </p><p>Sesampainya di sana kami langsung mencari ponsel yang barangkali ada yang lebih murah dan bagus. Tapi nyatanya tidak sama sekali. Jadilah kami mengambil ponsel ini.</p><p>Skip skip...</p><p>Seminggu saya menggunakan ponsel ini, saya kurang puas dengan beberapa fiturnya. Saya compare dengan apa yang sudah dibahas di tabloid yang sering saya baca bahwasannya ponsel ini telah mendukung fitur MMS dan Internet. Saya memang kurang mengerti apa itu internet. Yang saya ingin tahu adalah bagaimana supaya fitur dalam ponsel ini sepenuhnya dapat bekerja.</p><p>Karena rasa penasaran itu, pada minggu berikutnya saya mendatangi kembali counter tempat saya membeli ponsel agar dibantu mengaktifkan fitur tersebut. Tapi sayang sekali di sana saya tidak mendapatkan pelayanan yang cukup baik sehingga akhirnya disuruh untuk mendatangi gerai resmi Sony Ericsson yang berada tidak jauh dari tempat itu.</p><p>Dengan rasa penasaran ini saya mendatangi gerai resmi Sony Ericsson. Di sana saya disuruh menunggu setelah handphone saya diminta oleh teknisi. </p><p>Masih terus menunggu, tetap menunggu, dan menunggu dan menunggu, teknisipun keluar. Saya cukup lega. Tapi tidak seperti harapan saya karena di sinipun teknisi tidak dapat membantu dan menyarankan saya untuk mendatangi gerai Operator Seluler untuk mendapatkan bantuan setting internet.</p><p>Dengan perasaan kurang puas, sayapun mendatangi gerai operator tersebut. Kondisinya gerimis dan teman saya sudah ingin sekali pulang, karena waktu itu tidak menggunakan motor. Saya meminta kesempatan sekali lagi untuk mendatangi gerai operator tersebut.</p><p>Skip skip...</p><p>Sesampainya pada gerai operator, kembali saya menanyakan bagaimana untuk mengaktifkan fitur internet dan MMS pada ponsel saya. Kembali disuruh untuk menunggu, dan menunggu, dan menunggu dan menunggu.</p><p>Setelah lama menunggu, customer kembali mengatakan tidak dapat membantu dan malah menyuruh saya untuk ke gerai Sony Ericsson untuk mendapatkan bantuan. Nah saya juga baru saja selesai dari sana.</p><p>Ah berarti tidak bisa diapa-apakan. Sayapun pulang dengan beban.</p><p>(Bersambung)</p>', '["1","6"]', 'N', 'indonesia', 1451836089, 68),
(9, 10, 'wahhh-surprise-january-2016', 'Wahhh.... Surprise!!...', 'Ini yang selalu \r\nmembuat dia kesal... tiap menjelang akhir bulan, pasti kurang, pasti \r\nbingung, dan ujung-ujungnya pasti selalu aku saja yang disalahkan!&quot;.. \r\nProtes Rani dengan suara tinggi. &quot;Lho, kalau bukan kamu siapa lagi?.. \r\nJelas-jelas keuangan rumah ...', 'ekonomi,romantisme,rumahtangga', '<p><a href="http://www.dwitri.com/uploads/posts/bda9728f06eae866489abd7504e5348b.jpg" target="_blank"></a><a href="http://www.dwitri.com/uploads/posts/bda9728f06eae866489abd7504e5348b.jpg" target="_blank"></a><a href="http://www.dwitri.com/uploads/posts/bda9728f06eae866489abd7504e5348b.jpg" target="_blank"></a><a href="http://www.dwitri.com/uploads/posts/bda9728f06eae866489abd7504e5348b.jpg" target="_blank"></a><a href="http://www.dwitri.com/uploads/posts/bda9728f06eae866489abd7504e5348b.jpg" target="_blank"><img src="http://www.dwitri.com/uploads/posts/bda9728f06eae866489abd7504e5348b.jpg"></a></p><p>Ini yang selalu \r\nmembuat dia kesal... tiap menjelang akhir bulan, pasti kurang, pasti \r\nbingung, dan ujung-ujungnya pasti selalu aku saja yang disalahkan!".. \r\nProtes Rani dengan suara tinggi. "Lho, kalau bukan kamu siapa lagi?.. \r\nJelas-jelas keuangan rumah tangga kamu yang mengatur, tugasku hanya \r\nmencari dan memberi nafkah sesuai kemampuanku.. begitu kan pembagian \r\ntugasnya? Tak mau kalah mas Bram pun melempar argumentasi panjang lebar,\r\n  walau pun tidak dengan nada tinggi , raut wajahnya pun datar saja... </p><p>Justru sikap mas Bram yang seperti itulah, yang membuatnya makin kesal, \r\nsikap yang bagi Rani berarti apatis, masa bodo, dan tidak mau tahu. \r\nKenapa sih, dia tidak mau introspeksi atau paling tidak muncul  satu \r\nkeinginan  untuk sama-sama mencari solusi. Ingin sekali rasanya dia \r\nmeluapkan emosinya, tapi... "Uhhh..!" cuma itu yang terucap dari mulut \r\nRani ditambah.. "diskusi ini belum selesai ya, mas?!"  "Diskusi?" Bram \r\nmengerutkan keningnya, sambil menatap raut muka Rani yang terlihat \r\nkecut.  "Hehee, iyaa, iyaa nanti diskusinya kita sambung lagi, sekarang \r\ntolong buatkan aku kopi dulu ya?" Sambung Bram buru-buru sebelum Rani \r\nmelontarkan kekesalan lanjutannya. </p><p>Sebetulnya Bram, bukan tidak sadar \r\ndan tidak menghitung-hitung berapa penghasilannya dan berapa \r\npengeluarannya, tapi dia selalu berfikir, masa sih dia tidak boleh \r\nmenikmati hasil jerih payahnya dengan sedikit longgar.. Cuma ngopi, \r\nrokok, minuman suplement, tempe mendoan, somay, bakso mie ayam.. ahh \r\ncuma makanan kecil, tak sebanding dengan kelelahan fisik dan pikiran \r\nyang dia kerahkan untuk membayar semua makanan itu. </p><p>Dan lagi seharusnya \r\nRani, lebih pandai mengatur, atau mengerem pengeluaran, bukannya iya, \r\niya saja, ujung-ujungnya dia juga khan yang ikut pusing. Celotehan  dalam\r\n hati Bram, terhenti oleh suara gelas yang ditaruh Rani, di atas meja \r\nkerjanya, tak hanya itu Rani pun merapihkan sisa abu rokok, \r\nmenyingkirkan  gelas-gelas kotor sisa kopi dan teh yang dia seduh \r\nsendiri semalam. Hehehe. Lumayan cuma ada tiga gelas ,  bisik hatinya, dan biarlah itu\r\n kan memang tugas perempuan, bersih-rumah, bersih-bersih \r\nperabotan. </p><p>Sejauh ini, dimana letak kesalahanku coba?! Dasar saja Rani \r\nyang boros dan tidak pintar mengatur keuangan suami. Kesimpulan yang \r\nseperti itulah yang akhirnya muncul di kepalanya.. Waahhh...</p><p> "Mas, aku \r\nke pasar dulu ya, atau mau ikut?" Pagi-pagi sekali Rani sudah \r\nmembangunkanku. "Uhh, masih ngatuk Ran... Kamu sendiri aja ya? Et tapi \r\njangan lupa aku titip Semangka? Panas dalam nih aku"... kata Bram...</p><p>"Okelah kalau begitu, tapi nanti pas aku kembali dari pasar mas harus \r\nudah mandi, janji?".. "Iyaa..janji"  "Yaudah pergi dulu aku".. Rani pun \r\nsecepat kilat meluncur menuju pasar. Ini bukan awal bulan, tapi Rani \r\nbaru terima uang dari Bram, kebetulan jumlahnya lumayan, kurang lebih \r\njumlahnya adalah 1 juta rupiah. Dan inilah saat yang dinanti-nanti oleh Rani, dia \r\ningin menjalankan misi dan visinya sebagai perempuan, sebagai ibu rumah \r\ntangga, sebagai bendahara yang memiliki kewenangan untuk mengatur \r\nkeuangan dan kebutuhan rumah tangga. Bosan rasanya selalu dijadikan \r\nkambing hitam dan dicap tidak becus mengatur keuangan, boroslah.. Dah \r\nlengkap pokoknya.. </p><p>Jadi sekaranglah saat aku mesti bersikap tegas dan \r\nmemilah apa yang benar-benar harus dibeli dan mana yang tidak. Setibanya\r\n di pasar, segera dia mengeluarkan catatan kebutuhan pokok apa saja yang\r\n mesti dia beli, yang selama ini dia beli secara mengeteng dan secara \r\nncrit-crtit, yang setelah dia hitung-hitung jadi penyebab bocornya \r\nkeuangan rumah tangganya. Tidak hanya itu dia pun telah membuat \r\ncatatan dan jadwal kapan bisa mengkonsumsinya, misalnya saja, jadwal \r\nminum kopi itu maksimal 3 x sehari, minum teh manis 2 x sehari, makan \r\nmie instan 3 x per minggu. Hmm.. </p><p>Rani jadi tak kuasa menahan seyum, saat\r\n membayangkan bagaimana reaksi mas Bram nanti. 2 jam berikutnya Rani \r\nkembali dari pasar, mas Bram menepati janjinya, dia sudah mandi hingga \r\nterlihat bersih dan segar. Namun tak urung matanya terbelalak juga \r\nmelihat Rani pulang dengan membawa begitu banyak belanjaan, padahal dia \r\nkan cuma pesan semangka? "Bantuiin mas?!".. teriak Rani "Iya, \r\nsebentar?!" Bram membantu Rani menurunkan barang belanjaan dari atas \r\nangkot yang berhenti tepat di depan tempat tinggal mereka. </p><p>Dalam hati \r\nBram was-was, kalau begini pasti habislah uang yang diberikannya ke \r\nRani. Kali ini Rani benar-benar sedang tidak waras, lihat saja apa yang \r\nsudah dibelinya, beras 1 karung, mie instant 3 dus, kopi seduh 9 \r\nrenceng, gula, teh, telur, minyak goreng, sabun cuci, sabun mandi, pasta gigi, shampo, kentang, terigu, bumbu-bumbu, kerupuk, ikan \r\nasin, rokok.. yakin, uang yang dia berikan tadi pagi pasti ambalas... \r\nbisa-bisa kurang malah.. </p><p>"Duhh Ran.. Ran.. kamu ini kenapa sih, bisa \r\nhabis semua uang kita Ran... kamu belanja begini banyak haduhhh.. dan \r\nlagi mana semangka pesananku? "Inihh.." dengan tenang Rani mengasungkan \r\nsemangka pesanannya. "Et, tunggu ini kamu apa-apaan si Ran, uang kamu \r\nhambur-hamburkan gak jelas?!" Tegur Bram lagi. "Sudah serahkan saja sama aku \r\nmas, sekarang aku mau beresin belanjaan dulu, terus masak oke?" Rani \r\nterseyum puas ngeloyor ke dapur setelah mencubit pipi Bram. Meninggalkan\r\n Bram yang masih diliputi tanda tanya.</p><p> Hari itu Rani masak, kesukaan \r\nkeluarga, karena hatinya sedang senang masakan Rani pun jadi terasa \r\nlezat.  Hingga Bram pun melupakan apa yang tadi menjadi tanda tanya \r\nbesar di dalam hatinya. Baru sedikit mengutak-ngutik pekerjaannya Bram \r\nmerasa mengantuk, dan tertidur di ruang kerjanya. Dan baru bangun \r\nmenjelang sore, itu pun karena suara deringan dari HP nya, bukan karena Rani   yang membangunkan. </p><p>Telephon itu rupanya dari sahabatnya Hendra, \r\nyang ingin menawarkan project baru  yang honornya lumayan, tak ayal \r\ntawaran itu membuat wajah Bram terlihat sumringah. Apalagi pas menengok \r\nke sudut meja, Bram telah menjumpai kentang goreng kesukaannya dan \r\nditemani segelas kopi, wuihh, kadang-kadang Rani memang begitu amat \r\nmengerti kesukaannya.Wajah Bram makin sumringah saja...</p><p>Eit.. tapi.. tunggu \r\ndulu, apa-apaan ini? Mata Bram tertuju pada kertas berwarna biru muda \r\nyang di tempel di dinding, "Ihh apa-apaan si ini... Ran.. Rani!" tak jadi\r\n menyuprut kopi, Bram keluar mencari Rani. Panggilannya terhenti, \r\ndiurungkan niat untuk protes, dia melihat Rani tengah  menyirami tanaman\r\n di halaman, begitu asyiknya sampai-sampai dia tak mendengar suara \r\npanggilanku.Bram jadi tak sampai hati untuk mengganggunya. </p><p>Dia kembali \r\nke meja kerjanya, dan membaca lagi dengan teliti apa yang di tulis Rani.\r\n Ngopi 3 x sehari, pagi, sore dan malam maksimal 4 x Ngeteh 2 x siang \r\nsama sore (sebagai pengganti kopi) Ngerokok  maksimal 1 bungkus sehari \r\nJajan seminggu 2 x (hari Rabu dan hari Minggu) Dan seterusnya....dan seterusnya..masa \r\nharus sampai sebegitunya  sii Ran?... Bram menggaruk-garuk kulit kepalanya yang sama sekali tidak gatal, wahhh... ahhhh...<br><br> Selengkapnya ........</p>', '["10"]', 'Y', 'indonesia', 1453024333, 93),
(11, 10, 'benarkah-genius-itu-sama-dengan-gila-', 'Benarkah Genius Itu Sama Dengan Gila???', ' Seorang jenius dapat memecahkan masalah yang bahkan belum pernah dihadapinya dengan cepat karena punya banyak ide untuk mendapat \r\nsolusinya.Maybe....Why...Sebab orang yang genius sepertinya lebih dahulu mengetahui apa yang orang lain belum tahu. Jalan pikira...', 'albert einstein,genius,nyentrik', '<p><strong></strong> </p><p><a href="http://www.dwitri.com/uploads/posts/5990481067ec3145a5c4af7659bfa8f0.jpg" target="_blank"></a><a href="http://www.dwitri.com/uploads/posts/5990481067ec3145a5c4af7659bfa8f0.jpg" target="_blank"></a><a href="http://www.dwitri.com/uploads/posts/5990481067ec3145a5c4af7659bfa8f0.jpg" target="_blank"></a><a href="http://www.dwitri.com/uploads/posts/5990481067ec3145a5c4af7659bfa8f0.jpg" target="_blank"></a><a href="http://www.dwitri.com/uploads/posts/5990481067ec3145a5c4af7659bfa8f0.jpg" target="_blank"></a><a href="http://www.dwitri.com/uploads/posts/5990481067ec3145a5c4af7659bfa8f0.jpg" target="_blank"></a><a href="http://www.dwitri.com/uploads/posts/5990481067ec3145a5c4af7659bfa8f0.jpg" target="_blank"><img src="http://www.dwitri.com/uploads/posts/5990481067ec3145a5c4af7659bfa8f0.jpg"></a></p><p>Seorang jenius dapat memecahkan masalah yang bahkan belum pernah dihadapinya dengan cepat karena punya banyak ide untuk mendapat <br><br>\r\nsolusinya.</p><p>Maybe....</p><p>Why...</p><p>Sebab orang yang genius sepertinya lebih dahulu mengetahui apa yang orang lain belum tahu. Jalan pikirannya bisa saja selangkah atau bahkan sepuluh langkah di depan orang dengan kapasitas biasa-biasa saja.</p><p>Karenanya itu kadang dia terlihat tak peduli dengan apa yang terjadi di sekitarnya pada saat ini, sebab dengan kegeniusan yang dia miliki dia sudah bisa memprediksi apa yang terjadi. Semacam 1 + 1 hasilnya pasti 2,  1 - 1 = 0, hitam di campur putih, pasti hitam yang dominan, begitulahhh seterusnya dan seterusnya...</p><p>Lantas jika demikian enak tidak jadi orang yang genius?</p><p>Entahlah...</p><p>Maybe Enakkk...</p><p>Bisa juga malahan Gemess, Marahhh, Kesel... Habisnya orang di sekelilingnya ngira dia Gila.. dan ga percaya sama semua yang sudah jelas-jelas dia ketahui...</p><p>Tapi, tenang,  orang yang genius akan dengan mudah mengatasi semua itu coz dia bisa memprediksi apa yang akan  terjadi... Secara, jalan pikirannya khan 10 langkah ke depan, di bandingkan dengan kamu... kamu...kalian... kalian  dan juga saya..... Hehehhehe</p><p>Oya... biar jelas berikut saya kasih bonus referensi  tentang orang genius berikut ini;</p><p><strong>1.Mudah dan cepat menangkap apa yang dibicarakan orang meskipun seolah <br>tidak memperhatikan pembicara (Lebih fokus pada auditory dibanding <br>visualisasi).</strong><br><br><br>\r\nCukup beberapa kata kunci yang didengar di awal, seorang jenius sudah <br><br>\r\nmampu memahami seluruh isi materi yang disampaikan orang lain tanpa <br><br>\r\nperlu memperhatikan sampai akhir.<br><br><br>\r\n<strong>2. Memiliki kemampuan mengingat dan menghafal yang luar biasa.</strong> <br><br><br>\r\nSeorang jenius dapat menghafal dengan cepat sesuatu yang dilihat dan didengar. Contohnya menghafal nomor telepon teman.<br><br><br>\r\n<strong>3. Dapat berkonsentrasi dalam waktu yang lama pada hal-hal yang diminatinya.</strong><br><br><br>\r\n<strong>4. Sangat mudah bosan dengan rutinitas atau kegiatan yang tidak diminati.</strong><br><br><br>\r\nSeorang jenius mudah bosan dengan sesuatu yang sudah pernah dikerjakan atau dikenali.<br><br><br>\r\n<strong>5. Tertarik pada hal-hal yang ekstrem.</strong> <br><br><br>\r\nSeorang jenius juga tertarik dengan hal-hal berbau misteri, fantasi, <br><br>\r\nspiritual, magic, mistik dan fiksi (misal UFO, misteri atau dunia Hantu,<br><br>\r\n dsb).<br><br><br>\r\n<strong>6. Cenderung melanggar aturan.</strong> <br><br><br>\r\nSeorang jenius lebih suka melakukan hal dengan caranya sendiri dengan berperilaku tidak mengikuti kebiasaan lazim.<br><br><br>\r\n<strong>7. Pemikiran orisinil.</strong> <br><br><br>\r\nSeseorang yang jenius mulai dengan pemikiran yang terbuka, teratur, <br><br>\r\nmengambil perspektif-perspektif baru, mampu menguraikan masalah dan <br><br>\r\nmenyatukannya kembali dengan cara yang lebih baik.<br><br><br>\r\n<strong>8. Mensederhanakan perkara/ problem yang rumit.</strong><br><br><br>\r\n<strong>9. Lebih suka bergaul dengan yang lebih tua / dewasa.</strong><br><br><br>\r\nKarena merasa lebih cocok, dengan bahasa dan bahan perbincangan yang belum banyak dipahami anak seusianya.<br><br><br>\r\n<strong>10. Suka berfantasi, berinovasi dan membuat sesuatu/ penemuan baru yang belum dipikirkan orang sebelumnya.</strong><br><br><br>\r\n<strong>11. Dapat menyelesaikan puzzle yang rumit (semisal Sudoku, rubic atau<br>complex games lainnya) atau soal yang bersifat matematis/angka2 dan <br>mengutamakan logika, dalam waktu yang singkat.</strong><br><br><br>\r\n<strong>12. Konvensional dalam hal berbusana.</strong> <br><br><br>\r\nSeorang jenius tidak terlalu memikirkan penampilan.(Lihat Albert Einstein <a href="http://www.dwitri.com/uploads/posts/5990481067ec3145a5c4af7659bfa8f0.jpg" target="_blank"></a>).<br><br><br>\r\n<strong>13. Punya rasa ingin tahu yang tinggi dan sangat banyak akal.</strong> <br><br><br>\r\nSeorang jenius dapat memecahkan masalah yang bahkan belum pernah <br><br>\r\ndihadapinya dengan cepat karena punya banyak ide untuk mendapat <br><br>\r\nsolusinya.</p><p [removed]="margin-left: 40px;">OKE... Semoga artikel ini bermanfaat ya....</p><p><br><strong></strong></p>', '["10"]', 'N', 'indonesia', 1451903396, 91),
(12, 10, 'puisi-angin-pagi-', 'PUISI : ANGIN PAGI...', 'Pagi itu aki tua menyusuri duniaWajah sejuknya menerobos gulita Melangkah dan menjumput satu demi satu asaMenjelang siang langkahnya panjangSeyumnya pun rekah mengembangSatu karung besar remah perca tunai terbarter sudahSecangkir kopi dan asa...', '', '<p><a href="http://www.dwitri.com/uploads/posts/784172fcb76869c2bd151a0a20c3bc6b.jpg" target="_blank"><img src="http://www.dwitri.com/uploads/posts/784172fcb76869c2bd151a0a20c3bc6b.jpg"></a></p><p>Pagi itu aki tua menyusuri dunia</p><p>Wajah sejuknya menerobos gulita </p><p>Melangkah dan menjumput satu demi satu asa</p><p>Menjelang siang langkahnya panjang</p><p>Seyumnya pun rekah mengembang</p><p>Satu karung besar remah perca tunai terbarter sudah</p><p>Secangkir kopi dan asap yang mengepul membuatnya bungah</p><p>Baginya hidup begitu sederhana tuk dikayuh</p><p>Seperti angin yang menyapa dunia dengan sepoinya....</p>', '["10"]', 'N', 'indonesia', 1452128649, 30),
(13, 10, 'pemerintah-wajib-memikirkan-dampak-dan-nasib-1-jt-pns-yang-kena-pangkas', 'Pemerintah WAJIB  Memikirkan Dampak Dan Nasib 1 Jt PNS Yang Kena Pangkas', '\r\n \r\n Pemerintah bakal pangkas 1 juta  PNS, begitu bunyi artikel yang kini tengah marak beredar di berbagai media masa, tentang pemangkasan jt PNS dan penghematan keuangan negara sampai 37,1 trilyun,sebagaimana kutipan berita berikut ini.\r\n Jakarta -Menteri Pe...', 'Karir,Keuangan', '<p>\r\n <a href="http://www.dwitri.com/uploads/posts/1ac4695ee4b47b75180f3552557e2dc5.jpg" target="_blank"></a><a href="http://www.dwitri.com/uploads/posts/1ac4695ee4b47b75180f3552557e2dc5.jpg" target="_blank"></a><a href="http://www.dwitri.com/uploads/posts/1ac4695ee4b47b75180f3552557e2dc5.jpg" target="_blank"></a><a href="http://www.dwitri.com/uploads/posts/1ac4695ee4b47b75180f3552557e2dc5.jpg" target="_blank"></a><a href="http://www.dwitri.com/uploads/posts/1ac4695ee4b47b75180f3552557e2dc5.jpg" target="_blank"></a><a href="http://www.dwitri.com/uploads/posts/1ac4695ee4b47b75180f3552557e2dc5.jpg" target="_blank"></a><a href="http://www.dwitri.com/uploads/posts/1ac4695ee4b47b75180f3552557e2dc5.jpg" target="_blank"><img src="http://www.dwitri.com/uploads/posts/1ac4695ee4b47b75180f3552557e2dc5.jpg"></a></p><p>\r\n Pemerintah bakal pangkas 1 juta  PNS, begitu bunyi artikel yang kini tengah marak beredar di berbagai media masa, tentang pemangkasan jt PNS dan penghematan keuangan negara sampai 37,1 trilyun,sebagaimana kutipan berita berikut ini.</p><p>\r\n Jakarta -Menteri Pemberdayaan Aparatur Negara dan Reformasi Birokrasi\r\n (MENPAN RB) Yuddy Chrisnandi mengatakan pemerintah akan memangkas \r\njumlah aparatur sipil negara atau PNS dari saat ini 4,7 juta akan \r\nmenjadi hanya 3,5 juta atau sebesar 37,1%.</p><p>\r\n Perampingan jumlah PNS tersebut guna menghemat belanja pegawai di \r\nKementerian/Lembaga agar tidak lebih besar dari belanja pembangunan.</p><p>\r\n Jumlah aparatur sipil negara akan dikurangi dari 4,7 juta menjadi \r\n3,5 juta saja. Jumlah lebih sedikit dengan beban kerja yang sama. Jadi \r\nakan lebih berat dan dibutuhkan orang-orang dengan kompetensi yang \r\nlebih, kata Yuddy Chrisnandi ditemui di kantornya, Jakarta, Senin \r\n(4/1/2015).</p><p>\r\n Yuddy tidak menjelaskan lebih lanjut kapan target pemerintah mencapai\r\n angka jumlah PNS menjadi hanya 3,5 juta orang. Menurutnya, rata-rata \r\nanggaran belanja pegawai yang mencapai 42% masih terlalu gemuk.</p><p>\r\n Kita sedang melakukan kajian yang komprehensif, minggu ini kami \r\nsampaikan hasilnya. Kondisi saat ini pasti jumlah PNS sudah kelebihan, \r\ntambah Yuddy.</p><p>\r\n Menurut Yuddy, anggaran belanja pegawai semestinya tidak lebih besar \r\ndari belanja pembangunan. Perampingan jumlah PNS bisa mengurangi belanja\r\n pegawai untuk dialihkan ke belanja pembangunan yang lebih produktif.</p><p>\r\n Sekarang ibarat orang sesak nafas. Rata-rata 42% anggaran \r\nkementerian untuk belanja pegawai. Ada yang belanja pegawai 50% itu \r\nsudah lampu merah, masa lebih besar daripada belanja pembangunan, tutup\r\n Yuddy.\r\n <br>\r\n <a href="http://finance.detik.com/read/2016/01/04/123430/3110066/4/pemerintah-bakal-pangkas-jumlah-pns-371">http://finance.detik.com/read/2016/0jumlah-pns-371</a></p><p>\r\n Hemat Rp 3 Triliun, Menteri Yuddy Akan Pangkas 1 Juta PNS<br>\r\n SELASA, 05 JANUARI 2016 | 18:30 WIB</p><p>\r\n TEMPO.CO, Jakarta  Menteri Pendayagunaan Aparatur Negara dan \r\nReformasi Birokrasi (PAN-RB) Yuddy Chrisnandi sedang mengkaji rencana \r\npengurangan jumlah PNS (pegawai negeri sipil). Yuddy memperkirakan \r\njumlah pegawai negeri yang akan dipangkas bisa mencapai satu juta orang.\r\n Sekarang sedang kami kaji, mudah-mudahan dua pekan lagi selesai, ujar\r\n Yuddy di Istana Negara, Selasa, 5 Januari 2016.</p><p>\r\n Menurut Yuddy, pemangkasan dilakukan untuk efisiensi belanja negara \r\nkarena dirasakan adanya pemborosan anggaran dalam membiayai PNS. Ini \r\nuntuk rasionalisasi anggaran dan sumber daya manusia, katanya.</p><p>\r\n Rencana pemangkasan akan dilakukan dari PNS tingkat pusat hingga \r\ndaerah. Tentunya kami akan berkoordinasi dengan pihak-pihak terkait, \r\nseperti pemerintah daerah dan Kementerian Dalam Negeri, tuturnya.</p><p>\r\n Pemangkasan sekitar 1 juta PNS, kata Yuddy, dapat menghemat anggaran \r\nsampai Rp 3 triliun sebulan. Bayangkan saja, kalau rata-rata gaji PNS \r\nRp 3 juta per bulan plus tunjangan dikali satu juta, dan 12 bulan bisa \r\nhemat triliunan selama setahun, ucapnya.</p><p>\r\n Yuddy mengaku rencana ini belum dilaporkan kepada Presiden Joko \r\nWidodo. Ia segera melapor setelah menyelesaikan kajian yang \r\nkomprehensif. Namanya juga baru rencana, nanti kalau sudah ada hasil \r\nyang komprehensif baru kami laporkan, katanya.</p><p>\r\n Jumlah PNS seluruh Indonesia saat ini, berdasarkan laman Kementerian \r\nPAN-RB, mencapai 4,5 juta pada 2013. Mereka melayani 244,8 juta jiwa, \r\nsehingga rasionya 1,83 persen. Dua tahun kemudian, menurut Menteri \r\nYuddy, jumlah tersebut melonjak lebih dari 5 juta PNS. Pada Maret tahun \r\nlalu, Yuddy melontarkan pernyataan mengkaji penghentian perekrutan PNS \r\nselama lima tahun ke depan.\r\n <br>\r\n <a href="http://nasional.tempo.co/read/news/2016/01/05/078733257/hemat-rp-3-triliun-menteri-yuddy-akan-pangkas-1-juta-pns">http://nasional.tempo.co/read/news/2kas-1-juta-pns</a></p><p>\r\n Setelah membaca berita tersebut, tentu muncul berbagai opini dan pertanyaan di  masyarakat  khususnya PNS. Perampingan PNS dilihat sudut ekonomi tentu saja dianggap sebagai langkah yang positif.</p><p>\r\n Namun jangan lupa untuk memikirkan nasib, PNS yang tergusur, tentu mereka tak ingin jika dibuang begitu saja tanpa adanya kompensasi, seperti uang pensiun atau jaminan untuk melanjutkan roda kehidupan mereka selanjutnya. Bagaimana pun, mereka sedikit banyak telah dan pernah melakukan pengabdian pada pemerintah. Walaupun tidak sempurna..</p><p>\r\n  Dan saya yakin Presiden JOKOWI tidak mungkin akan melakukan satu hal yang bisa meninggalkan satu kenangan yang TIDAK  INDAH di hati para PNS yang  tergusur di era pemerintahannya.</p><p>\r\n Semoga<br>\r\n <a href="http://nasional.tempo.co/read/news/2016/01/05/078733257/hemat-rp-3-triliun-menteri-yuddy-akan-pangkas-1-juta-pns"></a></p>', '["6"]', 'Y', 'indonesia', 1452562044, 96),
(14, 10, '3-langkah-ampuh-usir-jerawat-dengan-masker-bawang-putih', '3 Langkah Ampuh Usir Jerawat Dengan Masker Bawang Putih', 'Benar apa ngga ya?.. Menurut pengalaman yang sudah mencoba katanya si benar, dan biar ga penasaran kamu bisa langsung mempraktekannya tapi sebelumnya   yuk intip 3 metode masker bawang putih untuk mengobati wajah berjerawat seperti yang dilansir oleh stylecraz...', 'Kehetan,Kecantikan', '<p><a href="http://www.dwitri.com/uploads/posts/08145b8c73f391c7a0cdbd3abe21cdb1.jpg" target="_blank"></a><a href="http://www.dwitri.com/uploads/posts/08145b8c73f391c7a0cdbd3abe21cdb1.jpg" target="_blank"></a><a href="http://www.dwitri.com/uploads/posts/08145b8c73f391c7a0cdbd3abe21cdb1.jpg" target="_blank"></a><a href="http://www.dwitri.com/uploads/posts/08145b8c73f391c7a0cdbd3abe21cdb1.jpg" target="_blank"><img src="http://www.dwitri.com/uploads/posts/08145b8c73f391c7a0cdbd3abe21cdb1.jpg"></a></p><p>Benar apa ngga ya?.. Menurut pengalaman yang sudah mencoba katanya si benar, dan biar ga penasaran kamu bisa langsung mempraktekannya tapi sebelumnya   yuk intip 3 metode masker bawang putih untuk mengobati wajah berjerawat seperti yang dilansir oleh stylecraze.com berikut ini.</p><p>Bawang putih yang menjadi salah satu bumbu masak utama yang tak pernah ketinggalan ada dalam menu-menu masakan Asia. Aroma dan rasa bawang putih menambah kesedapan masakan. Selain itu, bawang putih punya fungsi dan manfaat untuk tubuh yang tak diragukan lagi.</p><p>Bawang putih kaya akan komponen seperti sulfur, zat besi dan kalsium. Komponen-komponen ini berfungsi sebagai antibiotik dan antifungal yang baik untuk kesehatan kulit juga lho. Dengan konsumsi bawang putih secara rutin, kulit akan menjadi halus dan lembut. </p><p>Jadi  jika kamu punya masalah dengan jerawat, tak ada salahnya kamu mencoba mengobatinya dengan masker bawang putih. Ikuti langkah-langkah di bawah ini;<br><br><strong>Cara 1</strong><br><br>Potong bawang putih segar, geprak hingga keluar sarinya. Kemudian gosokkan bawang putih ke daerah yang berjerawat. Diamkan hingga 5 menit, bilas menggunakan air dingin.<br><br>Bawang putih menjadi salah satu bumbu masak utama yang tak pernah ketinggalan ada dalam menu-menu masakan Asia. Aroma dan rasa bawang putih menambah kesedapan masakan. Selain itu, bawang putih punya fungsi dan manfaat untuk tubuh yang tak diragukan lagi.<br><br><strong>Cara 2</strong><br><br>Ambil sari dari 2-3 bawang putih segar. Campurkan dengan cuka putih dengan perbandingan 1:1. Aplikasikan olesan ini pada wajah yang berjerawat.</p><p>Kandungan sulfur dan allicin dalam bawang putih mencegah infeksi dan mengurangi peradangan. Aliran darah pun lancar sehingga kulit akan tampak cerah dan segar. Wah, ternyata hanya dengan 2-3 siung bawang putih, jerawat kabur, kulit wajah makin cantik. Yuk cobain masker ini.</p><p><br><strong>Cara 3</strong><br><br>Untuk kamu yang merasa tak tahan dengan aroma menyengat bawang putih, dapat menggunakan cara ke-3 ini. Kupas beberapa siung bawang putih dan hancurkan. Tambahkan 1/2 sendok teh madu dan 2 sendok makan yoghurt. Oleskan campuran ini ke wajah dengan merata dan diamkan 20 menit. Setelah 20 menit, bilas wajah dengan air dingin.</p><p>Nah gimana, tertarik untuk mencoba tips sederhana dan murah ini? Oke, semoga artikel ini bermanfaat untuk kamu yang saat ini sedang bermasalah dengan jerawat..</p>', '["12"]', 'N', 'indonesia', 1452530013, 35),
(15, 10, 'akankah-masa-kejayaan-ronaldo-terlibas-gareth-bale-january-2016', 'Akankah Masa Kejayaan Ronaldo Terlibas Gareth Bale', 'Setelah Benitez, Madrid juga diprediksi bakal melengser Cristiano Ronaldo, ya masa kejayaan penyerang Real Madrid itu, dianggap sudah berakhir dan akan segera lewat, terbukti Tim lebih pilih Bale\r\n\r\n     \r\n     \r\n     \r\n     \r\n      \r\n \r\n      \r\nBerita santer ...', 'Ronaldo,Bale', '<p>Setelah Benitez, Madrid juga diprediksi bakal melengser Cristiano Ronaldo, ya masa kejayaan penyerang Real Madrid itu, dianggap sudah berakhir dan akan segera lewat, terbukti Tim lebih pilih Bale</p><article>\r\n<p><a href="http://www.dwitri.com/uploads/posts/dcf33f48a6a993d0ae76be6f76b152f6.jpg" target="_blank"></a><a href="http://www.dwitri.com/uploads/posts/dcf33f48a6a993d0ae76be6f76b152f6.jpg" target="_blank"><img src="http://www.dwitri.com/uploads/posts/dcf33f48a6a993d0ae76be6f76b152f6.jpg"></a></p><p><br><strong></strong></p><aside>\r\n     \r\n     \r\n     \r\n     \r\n      \r\n \r\n      \r\nBerita santer mengenai hal itu sebagaimana kami kutip dari viva.co.id seperti prediksi sebelumnya tentang Rezim Rafael Benitez di Real Madrid hanya bertahan 7 bulan. Sekarang klub menunjuk Zinedine Zidane sebagai penggantinya.</aside><p><br>Situasi\r\n ruang ganti Madrid diduga sudah kondusif sekarang. Para pemain lebih \r\nbisa menghormati Zidane sebagai pelatih karena statusnya sebagai legenda\r\n dan pemain besar pada masa lampau.<br><br>Tapi, di tengah kondisi yang \r\nmulai membaik, malah kembali mencuat isu tidak sedap. Ditengarai klub \r\nmenyusun agenda pembuangan pemain musim depan.<br><br>Anehnya, nama \r\nRonaldo ada dalam daftar teratas. CR7 dianggap sebagai pemain usang yang\r\n harus segera dilepas sebelum nilai jualnya merosot dimakan usia.<br><br>Tak\r\n cukup sampai di situ. Madrid pun sekarang akan mempromosikan Bale \r\nsebagai tumpuan masa depan. Padahal, sempat beredar rumor pemain asal \r\nWales itu yang hendak dilepas karena tidak disukai fans.<br><br>"Ronaldo\r\n adalah pemain yang diharapkan hengkang. Berbeda dengan Bale yang \r\ndianggap sebagai masa depan klub. Momen terbaik karier Ronaldo sudah \r\nlewat," kata sumber <em>Telegraph.</em><br><br>Dua klub disebut-sebut \r\nmenjadi calon pelabuhan baru Ronaldo. "PSG sangat berharap dan menjadi \r\nfavorit untuk memboyongnya, tapi MU juga tidak tinggal diam," \r\nsambungnya. (dyt)                           </p></article>', '["9"]', 'N', 'indonesia', 1453169663, 80);
INSERT INTO `posts` (`postID`, `contributor`, `postSlug`, `postTitle`, `postExcerpt`, `tags`, `postContent`, `categoryID`, `postHeadline`, `language`, `timestamp`, `visits_count`) VALUES
(16, 10, 'adab-jum-at-berkah-sesuai-sunnah-r-sulull-h-january-2016', 'Adab Jum''at  Berkah  Sesuai Sunnah Rsulullh', '\r\nBismillah..... Berikut ini beberapa adab yang harus\r\ndiperhatikan bagi setiap muslim yang ingin menghidupkan syariat Nabi\r\nshallallahu alaihi wa sallam pada hari Jumat.\r\n\r\n1. Memperbanyak Sholawat Nabi\r\nRasulullah shallallahu alaihi wa sallam\r\nbersabda...', 'Religi,Islam', '<p>\r\n<strong>Bismillah.....</strong></p><p><strong> </strong></p><p><strong></strong></p><p><strong></strong>Berikut ini beberapa adab yang harus\r\ndiperhatikan bagi setiap muslim yang ingin menghidupkan syariat Nabi\r\nshallallahu alaihi wa sallam pada hari Jumat.</p><p><a href="http://www.dwitri.com/uploads/posts/86b293707476458454026ac77a1b4876.jpg" target="_blank"></a><a href="http://www.dwitri.com/uploads/posts/86b293707476458454026ac77a1b4876.jpg" target="_blank"></a><a href="http://www.dwitri.com/uploads/posts/86b293707476458454026ac77a1b4876.jpg" target="_blank"><img src="http://www.dwitri.com/uploads/posts/86b293707476458454026ac77a1b4876.jpg"></a></p><p><br>\r\n<br>\r\n1. Memperbanyak Sholawat Nabi<br>\r\nRasulullah shallallahu alaihi wa sallam\r\nbersabda yang artinya,<br>\r\nSesungguhnya hari yang paling utama bagi kalian\r\nadalah hari Jumat, maka perbanyaklah sholawat kepadaku di dalamnya, karena\r\nsholawat kalian akan ditunjukkan kepadaku, para sahabat berkata: Bagaimana\r\nditunjukkan kepadamu sedangkan engkau telah menjadi tanah? Nabi bersabda:\r\nSesungguhnya Allah mengharamkan bumi untuk memakan jasad para Nabi. (Shohih.\r\nHR. Abu Dawud, Ibnu Majah, An-Nasai)<br>\r\nShalawat yang syari yaitu:<br>\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n\r\n<br>\r\nAllhumma shlli alaa muhammad wa alaa ali\r\nmuhammad,<br>\r\nkamaa shlayta alaa ibrhiim wa alaa ali\r\nibrhiim innaka hamidum majiid,<br>\r\nwa barik alaa muhammad wa alaa ali muhammad,<br>\r\nkamaa baarkta alaa ibrohiim innaka hamidum\r\nmajiid<br>\r\nartinya:<br>\r\nYa, Allah curahkanlah shalawat kepada Nabi\r\nMuhammad dan keluarganya, sebagaimana Engkau telah curahkan shalawat kepada\r\nNabi Ibrahim dan keluarganya. Sesungguhnya Engkau Maha Terpuji lagi Maha Mulia.\r\nYa Allah, curahkanlah barakah kepada Nabi Muhammad dan keluarganya, sebagaimana\r\nEngkau telah curahkan barakah kepada Nabi Ibrahim dan keluarganya. Sesungguhnya\r\nEngkau Maha Terpuji lagi Maha Mulia. (HR. Al Bukhari dan Muslim)<br>\r\n<br>\r\n2. Mandi Jumat<br>\r\nMandi pada hari Jumat wajib hukumnya bagi setiap\r\nmuslim yang balig berdasarkan hadits Abu Said Al Khudri, di mana Rasulullah\r\nbersabda yang artinya,<br>\r\nMandi pada hari Jumat adalah wajib bagi setiap\r\norang yang baligh. (HR. Bukhori dan Muslim).<br>\r\nMandi Jumat ini diwajibkan bagi setiap muslim\r\npria yang telah baligh, tetapi tidak wajib bagi anak-anak, wanita, orang sakit\r\ndan musafir. Sedangkan waktunya adalah sebelum berangkat sholat Jumat. Adapun\r\ntata cara mandi Jumat ini seperti halnya mandi janabah biasa. Rasulullah\r\nbersabda yang artinya,<br>\r\nBarang siapa mandi Jumat seperti mandi\r\njanabah. (HR. Bukhari dan Muslim)<br>\r\n<br>\r\n3. Menggunakan Minyak Wangi<br>\r\nNabi shallallahu alaihi wa sallam bersabda yang\r\nartinya,<br>\r\nBarang siapa mandi pada hari Jumat dan bersuci\r\nsemampunya, lalu memakai minyak rambut atau minyak wangi kemudian berangkat ke\r\nmasjid dan tidak memisahkan antara dua orang, lalu sholat sesuai yang\r\nditentukan baginya dan ketika imam memulai khotbah, ia diam dan mendengarkannya\r\nmaka akan diampuni dosanya mulai Jumat ini sampai Jumat berikutnya. (HR.\r\nBukhari dan Muslim)<br>\r\n<br>\r\n4. Bersiwak<br>\r\nAl-Imam Al-Bukhari dalam Shahihnya membuat Bab\r\nkhusus tentang ditekankannya bersiwak pada hari Jumat yaitu dalam dalam\r\nKitabul Jumuati Bab Ath-Thibbi Lil Jumuati, no. 880 dan Bab As-Siwaki Yaumul\r\nJumuati, no.hadits 887, 888, dan 889).<br>\r\n<br>\r\n5. Bersegera Untuk Berangkat ke Masjid<br>\r\nAbu Hurirh rdhiyallhu anhu berkata bahwa,\r\nNabi bersabda, Apabila hari Jumat, maka para malaikat berdiri di pintu masjid\r\nsambil mencatat orang yang datang dahulu, lalu yang dahulu (sesudah itu).\r\nPerumpamaan orang-orang yang datang pada waktu yang paling awal adalah seperti\r\norang yang berkurban seekor unta, berkurban sapi, berkurban kambing kibas,\r\nberkurban seekor ayam, lalu berkurban sebutir telur. Kemudian apabila imam\r\nsudah keluar (dalam satu riwayat: duduk 4/79), para malaikat itu melipat\r\nbuku-buku catatannya dan mendengarkan zikir (khutbah). (HR. Bukhari)</p><p><br>\r\nAnas bin Malik berkata,<br>\r\nKami berpagi-pagi menuju sholat Jumat dan tidur\r\nsiang setelah sholat Jumat. (HR. Bukhari).<br>\r\nAl Hafidz Ibnu Hajar berkata,<br>\r\nMakna hadits ini yaitu para sahabat memulai\r\nsholat Jumat pada awal waktu sebelum mereka tidur siang, berbeda dengan\r\nkebiasaan mereka pada sholat zuhur ketika panas, sesungguhnya para sahabat\r\ntidur terlebih dahulu, kemudian sholat ketika matahari telah rendah panasnya.\r\n(Lihat Fathul Bari II/388)<br>\r\n<br>\r\n6. Sholat Sunnah Ketika Menunggu Imam atau\r\nKhatib<br>\r\nAbu Huroiroh radhiallahu anhu menuturkan bahwa\r\nNabi Muhammad shallallahu alaihi wa sallam bersabda,<br>\r\nBarang siapa mandi kemudian datang untuk sholat\r\nJumat, lalu ia sholat semampunya dan dia diam mendengarkan khotbah hingga\r\nselesai, kemudian sholat bersama imam maka akan diampuni dosanya mulai jumat\r\nini sampai jumat berikutnya ditambah tiga hari. (HR. Muslim)<br>\r\n<br>\r\n7. Tidak Duduk dengan Memeluk Lutut Ketika\r\nKhatib Berkhotbah<br>\r\nSahl bin Muad bin Anas mengatakan bahwa\r\nRasulullah melarang Al Habwah (duduk sambil memegang lutut) pada saat sholat\r\nJumat ketika imam sedang berkhotbah. (Hasan. HR. Abu Dawud, Tirmidzi)<br>\r\n<br>\r\n8. Sholat Sunnah Setelah Sholat Jumat<br>\r\nRasulullah bersabda yang artinya,<br>\r\nApabila kalian telah selesai mengerjakan sholat\r\nJumat, maka sholatlah empat rakaat. Amr menambahkan dalam riwayatnya dari\r\njalan Ibnu Idris, bahwa Suhail berkata, Apabila engkau tergesa-gesa karena\r\nsesuatu, maka sholatlah dua rakaat di masjid dan dua rakaat apabila engkau\r\npulang. (HR. Muslim, Tirmidzi)<br>\r\n<br>\r\n9. Membaca Surat Al Kahfi<br>\r\nNabi bersabda yang artinya,<br>\r\nBarang siapa yang membaca surat Al Kahfi pada\r\nhari Jumat maka Allah akan meneranginya di antara dua Jumat. (HR. Imam Hakim\r\ndalam Mustadrok, dan beliau menshahihkannya)</p><p><br>\r\nDemikianlah sekelumit etika yang seharusnya\r\ndiperhatikan bagi setiap muslim yang hendak menghidupkan ajaran Nabi Muhammad\r\nshallallahu alaihi wa sallam ketika di hari Jumat. Semoga kita menjadi\r\nhamba-Nya yang senantiasa di atas sunnah Nabi-Nya dan selalu istiqomah di atas\r\njalan-Nya. aamiin.<br>\r\nWallahu alam bish shwwab...</p>', '["14"]', 'N', 'indonesia', 1453626592, 44),
(17, 10, 'ramalan-nasibnye-si-juned', 'Ramalan Nasibnye Si Juned', 'Siang itu Muhamad \r\nJunaedi, alias Juned tengah asyik membaca majalah, tepat di rublik \r\nramalan kesukaannya. Dan kali ini dia tengah menekuni satu demi satu \r\nramalan nasib sesuai shio anda di tahun 2016, tak berapa lama kemudian \r\nwajah Juned terlihat murung...', 'cerita ,fiksi', '<p><a href="http://www.dwitri.com/uploads/posts/7051d72bb61d2a887d5a8826c803a202.jpg" target="_blank"><img src="http://www.dwitri.com/uploads/posts/7051d72bb61d2a887d5a8826c803a202.jpg"></a></p><p>Siang itu Muhamad \r\nJunaedi, alias Juned tengah asyik membaca majalah, tepat di rublik \r\nramalan kesukaannya. Dan kali ini dia tengah menekuni satu demi satu \r\nramalan nasib sesuai shio anda di tahun 2016, tak berapa lama kemudian \r\nwajah Juned terlihat murung seperti tak memiliki semangat hidup.</p><p>"Woy, sepet banget tu muka?!" Sapa Rohim teman sekerjanya Juned. Ditanya\r\n begitu, Juned bukannya menjawab, tapi malah mengasungkan majalah yang \r\nbarusan dibacanya "Males gue Im.." lanjutnya "Kenape?!" Tanya Rohim \r\nlagi. "Neh, baca dah!"  Mata Rohim pun dengan lancar segera  membaca \r\nkalimat yang ditunjuk Juned. "Udeh?!" Tanya Juned. "Udeh... " Kata Rohim\r\n datar, sementra tangannya membalik-balik halaman lain dari majalah itu.</p><p>"Benernye elu baca apa kagak si Im.. kok reaksi lu adem aje?" Tanya \r\nJuned ragu "De''elahh, baca.."  Jawab Rohim sembari mata terus tertuju \r\npada artikel mengenai penemuan batu giok seberat 20 ton. "Menurut \r\npendapat lu gimana Im?"...  "Alhamdulillah dah, kalau gue si kagak bakal\r\n nolak kalau dikasi mah.." Juned mengerutkan dahinya. "Et dah lu malah \r\nbaca itu, nasib gue gimana niii..?!" Teriak Juned, sembari merampas \r\nmajalah yang dibaca Rohim. "Bujug, bocah ngapa sii.. orang lagi serius \r\nbaca, maen tarik aja?!" Juned, tak menanggapi protes sahabatnya Rohim, \r\nbukan itu saja mukanya pun makin ditekuk.</p><p>"Duhh, tong..tong, kelakuan lu kaya bocah kemaren sore aje lu. Segala \r\nramalan kaya begitulah ditakutin!" Kata Rohim kesal "Gimana gue kagak \r\ntakut, disitu ditulis dari Januari sampai Desember, gue bakal terus \r\nditimpa musibah, segala usaha apa juga kagak bakalan berhasil. Itu \r\nlantaran katenye shio gue taun ini ciong!" Kata Juned dengan suara mirip\r\n orang putus asa.</p><p>Melihat sikap Juned yang seperti itu, Rohim jadi kasihan bercampur \r\nkesal. "Jadi apa yang bisa gue bantu?".. "Apa kek, advis kek..." "Ohh, \r\nkalo gitu dengerin nih, advis gue. Pertama, lu kagak boleh percaya ama \r\nramalan, dosee.. lu tau khan? Kedua lu boleh aje percaya, tapi.... " \r\n"Tapi ape?!" Sergap Juned, karena Rohim tidak juga menyelesaikan \r\nkalimatnya.</p><p>Sejenak Rohim menghela nafas, seperti tengah berfikir keras. "Tapi \r\njangan lu telen mentah-mentah semuanye, kaya orang kaga punya Tuhan aje.\r\n Informasi jelek yang lu dapet jangan bikin lu malah jadi lembek. Justru\r\n sebaliknye, ibarat kata lu lagi jalan, pas di tengah jalan ada yang \r\nngasih tau, kalo di depan lu banyak duri, ya pake dong otak  lu. \r\nBegimane caranye biar  kaki lu kaga kene duri, cari jalan keluarnye. \r\nMisalnye aje lu siapin sepatu besi kek, atau lu bawa sapu, nah lu \r\nsapuiin dah tu duri. Jangan belon-belon lu udah nyerah, kaya ayam sayur.\r\n Mikir!" Telunjuk Rohim menunjuk ke dahinya sendiri.</p><p>Seperti terhipnotis, Juned cuma bisa terbengong-bengong mendengarkan \r\nkalimat yang keluar dari mulut sahabatnya Rohim. "Sekarang paham khan \r\nlu? Yaudah, begitu aje,   ni majalah gue pinjem yeh?" tanpa menunggu \r\njawaban Juned, Rohim pun meraup majalah yang berada di tangan Juned. \r\nKetika tersadar Rohim sudah ngeloyor pergi.. "Eh, Im..Im..! Maja.lahhnye..." \r\nJuned tak meneruskan kalimatnya buat sejenak, "Majalah gue, buat lu \r\naje.Im...." Lanjutnya dalam hati....<br><br> Selengkapnya.....  </p>', '["10"]', 'N', 'indonesia', 1452214398, 114),
(18, 10, 'rahasia-kecantikan-siti-aisyah-ra', 'Rahasia Kecantikan Siti Aisyah, RA', 'Sudah menjadi rahasia umum, jika wanita selalu berusaha menjaga penampilan agar senangtiasa terlihat cantik, karena begitulah  kodrat \r\ndari seorang wanita. Bukan hanya itu saja, setiap wanita menginginkan tubuh yang ideal, dan \r\nterlebih lagi kesehatan tubuh ...', 'Kesehatan,Kecantikan', '<p><a href="http://www.dwitri.com/uploads/posts/b1b02f5e3b50886bdfcff6b5b1d1afb4.jpg" target="_blank"></a><a href="http://www.dwitri.com/uploads/posts/b1b02f5e3b50886bdfcff6b5b1d1afb4.jpg" target="_blank"><img src="http://www.dwitri.com/uploads/posts/b1b02f5e3b50886bdfcff6b5b1d1afb4.jpg"></a></p><p>Sudah menjadi rahasia umum, jika wanita selalu berusaha menjaga penampilan agar senangtiasa terlihat cantik, karena begitulah  kodrat \r\ndari seorang wanita. Bukan hanya itu saja, setiap wanita menginginkan tubuh yang ideal, dan \r\nterlebih lagi kesehatan tubuh pun tetap harus terjaga.<br>\r\n<br>\r\nBuat wanita muslimah, kalian bisa mencontohi rahasia kecantikan istri \r\ndari Rasulullah SAW yaitu Siti Aisyah ra. Rahasianya, Aisyah ra cuma \r\nmenggunakan mentimun dan kurma basah untuk menjaga penampilannya agar \r\nmemiliki tubuh yang ideal.</p><p><strong>Sebagaimana disebutkan dalam sebuah hadist, <em>"Aisyah\r\n RA berkata: Ibuku mengobatiku agar aku kelihatan gemuk, saat dia hendak\r\n mempertemukan aku dengan Rasulullah SAW dan usaha itu tidak membuahkan \r\nhasil sehingga aku memakan mentimun dengan kurma basah. Kemudian aku \r\nmenjadi gemuk dengan bentuk yang ideal"</em>. (HR Ibnu Majah).</strong><br>\r\n<br>\r\nRasulullah SAW pun pernah menggunakan cara ini untuk menjaga kesehatannya.<br>\r\n<br>\r\nDisebutkan dalam hadist, Abdullah bin Ja''far mengatakan; untuk menjaga \r\nkesehatannya, Rasulullah SAW suka makan mentimun dicampur dengan kurma. \r\nKisah ini diriwayatkan oleh Tirmidzi dalam salah satu haditsnya.<br>\r\n<br>\r\n<strong><em>"Sesungguhnya Rasulullah SAW memakan mentimun dengan kurma masak" </em>(HR Tirmidzi).<br>\r\n<br>\r\n[<em>Dikutip dari buku pintar kedokteran nabi karya Ibnu Qoyyim al-jauziyah hal-431, terbitan PT Fathan Prima Media</em>]</strong><br>\r\n<br>\r\nNah, sekarang jadi tahu khan, bahwa untuk tampil menawan dengan memiliki tubuh yang ideal tak perlu \r\ndengan biaya yang mahal. Dengan cara yang telah dicontohkan oleh Aisyah \r\nra, maka anda sudah bisa memiliki tubuh yang sehat dan ideal. Tapi \r\ningat! Kecantikan seorang wanita muslimah bukan untuk dipamer atau \r\ndipertontonkan kepada bukan mahramnya. </p><p>Bagaimana pun juga seorang wanita\r\n muslimah wajib untuk menutup aurat, karena itulah kecantikan yang \r\nsesungguhnya dihadapan Allah SWT. Wassalam... Semoga artikel ini bermanfaat...</p>', '["12"]', 'N', 'indonesia', 1452529903, 28),
(19, 10, 'insyaf-vendor-kanada-akhirnya-rilis-blackberry-priv-siap-tandingi-pasaran-android', 'INSYAF Vendor Kanada Akhirnya Rilis Blackberry Priv Siap Tandingi Pasaran Android', 'Jika ditanya siapa yang tidak kenal Blackberry? Jawabnya pasti hampir semua kenal, paling tidak pernah melihat... :D Yaa, gadget terobosan baru yang beberapa tahun lalu sempat merajai ponsel dengan model baru.Namun setelah gempuran sistem operasi Android meraj...', 'Teknologi', '<p><img src="http://www.dwitri.com/uploads/posts/bc07c49a3f48c62c638f2257843cbb1c.jpg"></p><p>Jika ditanya siapa yang tidak kenal Blackberry? Jawabnya pasti hampir semua kenal, paling tidak pernah melihat... :D Yaa, gadget terobosan baru yang beberapa tahun lalu sempat merajai ponsel dengan model baru.</p><p>Namun setelah gempuran sistem operasi Android merajalela dunia \r\ngadget, Blackberry kian meredup. Hal itu karena Blackberry tetap \r\nmempertahankan OS eksklusif di pasaran.</p><p>Nah, kali ini Blackberry tampaknya insyaf. Vendor asal Kanada ini \r\nmemperkenalkan produk teranyar Blackberry Priv yang rencananya dilepas \r\nke pasar ponsel Indonesia dalam waktu dekat.</p><p>Diganjar harga sekitar Rp 9,5 juta, Blackberry Priv siap bersaing \r\ndengan ponsel android lain. Blackberry pasalnya kini sudah bertobat, ia \r\ntak lagi eksklusif  dan mencoba menggunakan OS Android 5.1 Lollipop.</p><p>Desain Blackberry Priv, misalnya, kini menganut layar yang lebih \r\nlebar, ukuran 5,5 inci dengan teknologi layar menggunakan Amoled \r\ndankerapatan resolusinya 1440p dan memiliki bentuk fisik melengkung dari\r\n sisi kiri dan kanannya. Seperti pada semua produk Blackberry umumnya.</p><p>Dari sisi dapur pacu, Blackberry Priv juga telah dilengkapi dengan \r\nprosesor system on chip (Soc) Qualcomm Snapdragon 808 berkecepatan 1,8 \r\nGHz dan didukung dengan RAM berukuran 3 GB ditambah memori internal \r\nsebesar 32 GB.</p><p>Oke.. move on Blackberry!..</p>', '["3"]', 'N', 'indonesia', 1452216890, 21),
(20, 10, 'jokowi-sinyalkan-reshuffle-siapakah-menteri-yang-layak-berhenti-', 'Jokowi Sinyalkan Reshuffle:  Siapakah Menteri Yang Layak Berhenti??', 'Siapa menteri yang pantas dicopot dari jabatannya? Pertanyaan serupa itu kencang berhembus menandai kencangnya sinyal reshuffle kabinet jilid II terus mengemuka. Diyakini tak lama \r\nlagi Presiden Joko Widodo (Jokowi) bakal mengumumkan perombakan menteri.Kamis\r...', 'Reshuffle,Kabinet', '<p><a href="http://www.dwitri.com/uploads/posts/ecfb0b36bf4794da5360162e95e1a82d.jpg" target="_blank"><img src="http://www.dwitri.com/uploads/posts/ecfb0b36bf4794da5360162e95e1a82d.jpg"></a></p><p>Siapa menteri yang pantas dicopot dari jabatannya? Pertanyaan serupa itu kencang berhembus menandai kencangnya sinyal reshuffle kabinet jilid II terus mengemuka. Diyakini tak lama \r\nlagi Presiden Joko Widodo (Jokowi) bakal mengumumkan perombakan menteri.<br><br>Kamis\r\n (7/1) kemarin, Jokowi mengundang para relawan dari berbagai kelompok \r\nmakan siang bersama di Istana Kepresidenan. Di pertemuan itu hadir \r\nkelompok Projo, Bara JP, Seknas Jokowi.<br><br>Agenda makan siang itu \r\nberlangsung sekitar dua jam dalam suasana santai. Meski begitu, berbagai\r\n isu yang tengah mengemuka juga dibahas para relawan bersama Jokowi, \r\ntermasuk soal reshuffle.<br><br>Baca juga: <a href="http://news.detik.com/read/2016/01/07/120319/3112722/10/jokowi-saya-harap-semua-bekerja-soal-reshuffle-biar-jadi-urusan-saya">Jokowi: Saya Harap Semua Bekerja, Soal Reshuffle Biar Jadi Urusan Saya</a><br><br>"Banyak\r\n masalah-masalah yang dibahas, termasuk soal reshuffle, kinerja \r\nmenteri," kata Ketua Umum Projo Budi Arie saat dikonfirmasi. Menurutnya \r\npara relawan ikut memberi masukan kepada Jokowi karena kinerja \r\npemerintahan banyak yang harus diperbaiki agar hasilnya maksimal.<br><br>"Memang\r\n kita diskusi tentang kinerja pemerintahan. Kita menilai banyak yang \r\nharus diperbaiki. Presiden sudah tahu," katanya. Lalu, bagaimana sikap \r\nJokowi soal reshuffle?<br><br>"Intinya, demi kinerja, Presiden sudah \r\npunya bahan yang komprehensif. Presiden sudah punya bahan dan saya yakin\r\n presiden punya keputusan-keputusan terbaik," jelas dia. <br><br>Baca juga: <a href="http://news.detik.com/read/2016/01/05/113332/3110857/10/ketum-pan--soal-reshuffle-kami-menghormati-hak-prerogatif-presiden">Ketum PAN: Soal Reshuffle, Kami Menghormati Hak Prerogatif Presiden</a><br><br>Meski\r\n demikian, lanjut Arie, dia dan para relawan tak mau menekan-nekan atau \r\nmendikte Presiden terkait perombakan Kabinet Kerja. Biarlah Jokowi yang \r\nnantinya mengambil keputusan.<br><br>Di tengah menguatnya sinyal \r\nreshuffle ini, Wakil Presiden Jusuf Kalla (JK) juga berencana menemui \r\nKetua Umum PDIP Megawati Soekarnoputri, Kamis (7/1). Namun dia enggan \r\nmenjelaskan detail rencananya itu, termasuk juga apakah dirinya akan \r\nmembicarakan soal isu reshuffle dengan Mega.<br><br>Baca juga: <a href="http://news.detik.com/read/2016/01/04/124415/3110071/10/manuver-menteri-yuddy-chrisnandi-di-tengah-isu-reshuffle-jilid-ii">Manuver Menteri Yuddy Chrisnandi di Tengah Isu Reshuffle Jilid II</a><br><br>"Yang\r\n reshuffle kan bukan Bu Mega. Pak Jokowi yang reshuffle," ujar JK di \r\nkantor Wakil Presiden, Jl Medan Merdeka Utara, Jakarta Pusat, Kamis \r\n(7/1). Namun sayang niat pertemuan JK dengan Mega tertunda lantaran Mega\r\n masih kelelahan karena baru pulang dari Yogyakarta.<br><br>"Malam hari \r\nini (Mega) tidak ada agenda ketemu dengan Wapres," kata Sekjen PDIP \r\nHasto Kristiyanto di depan rumah Mega, Jl Teuku Umar, Jakarta Pusat, \r\nKamis (7/1) malam. Dia mengaku kapan penjadwalan ulang pertemuan antara \r\nJK dan Mega. Jelasnya, dia menyebut Mega dan para petinggi PDIP lainnya \r\nmemang tengah disibukkan mengurus persiapan Rakernas I PDIP yang akan \r\ndilaksanakan pada 10-12 Januari mendatang di JI Expo Kemayoran, Jakarta.\r\n Tim Komunikasi Presiden Ari Dwipayana menyatakan Jokowi akan menghadiri\r\n dan berpidato di acara tersebut.<br><br>Baca juga: <a href="http://news.detik.com/read/2016/01/04/121753/3110025/10/soal-reshuffle-seskab-memang-ada-yang-minta-2-nama">Soal Reshuffle, Seskab: Memang Ada yang Minta 2 Nama</a><br><br>Jokowi\r\n sendiri sudah beberapa kali bicara soal isu reshuffle jilid II yang \r\nterus mengemuka ini. Dia menegaskan, reshuffle adalah hak prerogatifnya \r\nsebagai Presiden. Dia tidak ingin didikte-dikte oleh siapapun. Dia pun \r\nmeminta semua menteri tetap bekerja dan tidak terpengaruh isu ini.<br><br>"Saya\r\n harap kita semua bekerja saja dengan baik. Urusan angkat dan ganti \r\nmenteri biar jadi urusan saya-Jkw," cuit Jokowi lewat akun Twitter-nya \r\n@jokowi, Kamis (7/1). <br><br>Kabar reshuffle kabinet jilid II ini \r\nsendiri kembali berhembus saat Ketua DPP PAN Aziz Subekti pada awal \r\nJanuari ini mengaku partainya bakal mendapat dua pos menteri. Aziz \r\nmengaku mendapat info dari kalangan Istana bahwa perombakan kabinet akan\r\n dilakukan pada Januari 2016. Pengakuan Aziz itu sebenarnya sudah \r\ndibantah oleh rekan separtainya, namun toh isu soal perombakan kabinet \r\ntetap mencuat... Bagaimana hasil akhirnya?? Kita tunggu saja hasil akhirnya dalam waktu dekat ini. </p>', '["4"]', 'N', 'indonesia', 1452218073, 29),
(22, 10, 'sukses-jadi-juragan-melalui-daur-ulang-limbah-plastik', 'Sukses Jadi Juragan Melalui Daur Ulang Limbah Plastik', 'Apakah anda saat ini masih menganggur, baru di PHK dan masih bingung \r\nmencari-cari celah untuk menghidupkan kembali mesin keuangan anda. Jika \r\nitu kondisi anda saat ini, coba tolong luang waktu anda untuk melongok \r\npeluang bisnis yang satu ini.Lautan sampah...', 'Keuangan', '<p><img src="http://www.dwitri.com/uploads/posts/25bbf1b8c2909760a56fa9b6f67627dd.jpg"></p><p>Apakah anda saat ini masih menganggur, baru di PHK dan masih bingung \r\nmencari-cari celah untuk menghidupkan kembali mesin keuangan anda. Jika \r\nitu kondisi anda saat ini, coba tolong luang waktu anda untuk melongok \r\npeluang bisnis yang satu ini.</p><p>Lautan sampah plastik adalah harta bagi kita </p><p>Yaaa, peluang\r\n bisnis sampah plastik....  Memang bagi sebagian orang masih dianggap bisnis yang \r\nkotor dan tidak ada masa depannya, sebagian pemilik modal besar lebih \r\nmenginvestasikan uang mereka pada bisnis-bisnis real-estate , bisnis \r\nrestauran ataupun di arahkan pada investasi tanah. Mereka tidak sadar \r\nbagaimana peluang bisnis sampah plastik di negeri ini.</p><p>\r\nKebiasaan\r\n buruk yang dilakukan oleh masyarakat indonesia merupakan harta yang \r\nmelimpah bagi seseorang yang melihat peluang pada bisnis sampah plastik \r\nini, Bisa kita lihat pada kota-kota besar , selokan-selokan serta TPA di\r\n kota-kota besar di negeri ini, puluhan ton limbah plastik diproduksi \r\noleh masyarakat di ibu kota jakarta. </p><p>Menurut\r\n laporan dari pebisnis limbah plastik yang enggan disebut namanya yang \r\nberdomisili di kota bandung, permintaan bahan baku biji plastik dari \r\nnegara-negara asia seperti Hongkong dan China masih sangat jauh dari \r\ncukup. Bayangkan saja , Hongkong saja per minggunya memerlukan 12 \r\ncontainer , dan Indonesia masih belum bisa memenuhi permintaan tersebut.\r\n Bisa anda bayangkan bagaimana jika kita mampu melirik peluang kearah \r\nsana. </p><p><a href="http://mesinsakti.blogspot.com/2015/03/mesin-mesin.html">mesin</a> pengolah limbah </p><p>\r\nIni\r\n dikhususkan jika kita hendak mengolah sendiri semua limbah plastik \r\ntersebut. semua limbah plastik tersebut kita giling menggunakan mesin \r\npencacah plastik. adapun peralatan mesin-mesin yang dibutuhkan adalah \r\nsebagai berikut:</p><p>\r\n<u><a href="http://mesinsakti.blogspot.com/2014/10/mesin-pencacah-plastik.html">Mesin pencacah plastik ; </a></u></p><p>\r\nPeluang bisnis sampah plastik yang akan kita tekuni  adalah memproduksi sendiri limbah plastik yakni <a href="http://mesinsakti.blogspot.com/2014/09/mesin-giling-plastik.html">menggiling plastik</a>\r\n menjadi serpihan kecil-kecil . Mesin ini berguna untuk mencacah \r\nplastilkdari plastik yang ukurannya besar dan tidak beraturan , dicacah \r\nuntuk dijadikan serpihan-serpihan kecil-kecil biasanya ukurannya lebih \r\nkecil dari 20 mm </p><p>\r\n<u><a href="http://mesinsakti.blogspot.com/2014/12/mesin-pencuci-plastik.html">Mesin pencuci plastik </a></u></p><p>\r\nIni\r\n masih kelanjutan dari peluang bisnis sampah plastik diatas , setelah \r\nsampah   plastik tersebut digiling maka langkah selanjutnya adalah di \r\ncuci sebab biasanya pabrik plastik tidak mau menerima produk kita jika \r\nmasih dalam keadaan kotor, untuk itu diperlukan sebuah mesin yang \r\ndinamakan mesin pencuci plastik. Kebanyakan plastik saat kita beli dari <a href="http://id.wikipedia.org/wiki/Pemulung">pemulung</a> masih dalam keadaan kotor, untuk itu perlu dicuci menggunakan mesin pencuci plastik.</p><p>\r\n<u><a href="http://mesinsakti.blogspot.com/2014/12/mesin-pengering-plastik.html">Mesin pengering plastik </a></u></p><p>\r\nmesin\r\n ini berupa mesin centris atau juga mesin pengering horizontal , mesin \r\ncentris cara kerjanya sama seperti pengering mesin cuci di rumah kita, \r\nia hanya mengeringkan serpihan limbah plastik yang telah kita cuci tadi,\r\n namun tidak menghasilkan serpihan plastik yang benar-benar kering, dan \r\nuntuk menghasilkan serpihan plastik yang benar-benar kering diperlukan \r\nsebuah alat yang namanya mesin dryer horizontal , alat ini dilengkapi \r\ndengan pemanas sehingga limbah plastik yang sudah dicacah akan \r\nbetul-betul kering. </p><p>\r\nItulah\r\n beberapa peluang bisnis sampah plastik, sungguh sangat menjanjikan \r\napalagi ditambah kebiasaan buruk masyarakat kita yakni dengan \r\nsembarangan membuang limbah plastik dimana saja. Semoga artikel singkat \r\nini bermanfaat.Selamat mencoba, selamat berkarya... Sukses ya...</p>', '["5"]', 'N', 'indonesia', 1452220278, 26),
(23, 10, 'ahok-usai-pecat-dirut-transjakarta-kini-giliran-kasatpol-pp-jakarta-pusat', 'AHOK Usai Pecat Dirut Transjakarta Kini Giliran Kasatpol PP Jakarta Pusat', 'Setelah memecat Direktur Transjakarta  yang dianggap tak becus kerja seperti yang diungkapkan beberapa waktu yang lalu, kini  Kasatpol PP Jakarta Pusat yang menunggu gigilan menerima sanksi pemecatan.Gubernur\r\n DKI Jakarta, Basuki Tjahaja Purnama (Ahok) mengak...', 'Ahok,Jakarta', '<p><img src="http://www.dwitri.com/uploads/posts/998145057a2b6e8d9d79a65cdce654de.jpg"></p><p>Setelah memecat Direktur Transjakarta  yang dianggap tak becus kerja seperti yang diungkapkan beberapa waktu yang lalu, kini  Kasatpol PP Jakarta Pusat yang menunggu gigilan menerima sanksi pemecatan.</p><p>Gubernur\r\n DKI Jakarta, Basuki Tjahaja Purnama (Ahok) mengaku akan mencopot Kepala\r\n Satuan Polisi Pamong Praja (Kasatpol PP) Jakarta Pusat, Yadi Rusmayadi.\r\n Alasannya, Yadi dianggap tidak bisa membersihkan pedagang kaki lima \r\n(PKL) yang kembali membanjiri kawasan Tanah Abang, Jakarta Pusat, \r\nsetelah sempat ditertibkan beberapa waktu lalu<strong>.</strong></p><p><br>"Makanya besok kita mau copot Kasatpol PP nya," kata Ahok di Balai Kota Jakarta, Kamis (7/1/2016) kemarin.<br><br>Mantan Bupati Belitung Timur itu mengatakan pihaknya akan mencari orang yang tepat untuk mengganti posisi Yadi.<br><br>"Kita akan ganti yang lebih kenceng," kata Ahok.<br><br>Lebih\r\n lanjut, Ahok memastikan akan melakukan perombakan di jajaran struktur \r\nSatpol PP. Terlebih dahulu, kata Ahok, Yadi akan digantikan dari \r\njabatannya sebagai Kasatpol PP Jakarta Pusat karena telah memasuki masa \r\npensiun bulan depan.<br><br>"Pusat dulu, nanti yang pusat kan pensiun bulan depan," katanya. Hehehe, yang lain biar nganti dulu ya pak?...</p>', '["7"]', 'N', 'indonesia', 1452223816, 30),
(24, 10, 'suami-suami-yang-tidak-pernah-dimarahi-istri', 'SUAMI-SUAMI YANG TIDAK PERNAH DIMARAHI ISTRI', ' Tolong jujur. Siapakah para suami di sini yang belum pernah dimarahi istrinya? tanya saya kepada para peserta laki-laki\r\n dalam sebuah Pelatihan Wonderful Family. Peserta Pelatihan ini adalah \r\npasangan suami istri yang usia pernikahannya di atas sepulu...', 'Konsultasi,Keluarga', '<p><img src="http://www.dwitri.com/uploads/posts/b1286c2bd5c130e0c29c584942ee22a9.jpg"></p><p> Tolong jujur. Siapakah para suami di sini yang belum pernah dimarahi istrinya? tanya saya kepada para peserta laki-laki\r\n dalam sebuah Pelatihan Wonderful Family. Peserta Pelatihan ini adalah \r\npasangan suami istri yang usia pernikahannya di atas sepuluh tahun.</p><p>\r\n Seorang lelaki dengan mantap mengangkat tangannya. Hanya satu orang \r\nsaja, dari sekitar enampuluh peserta lelaki yang mengikuti Pelatihan \r\ntersebut. Tentu saja saya merasa surprise, karena lebih dari sepuluh \r\ntahun menikah dan dia belum pernah dimarahi istrinya. Untuk itu, sebagai\r\n apresiasi, saya meminta kepada lelaki tersebut untuk maju ke depan \r\nforum bersama istrinya.</p><p> Benar anda belum pernah dimarahi istri selama usia pernikahan anda? tanya saya kepada sang suami.</p><p> Benar Pak Cah. Alhamdulillah istri saya baik banget. Saya tidak pernah melihatnya marah, jawabnya mantap.</p><p>\r\n Benarkah demikian? Bisa anda ceritakan bagaimana tipsnya lebih dari \r\nsepuluh tahun menikah dan anda tidak pernah memarahi suami, tanya saya \r\nkepada sang istri.</p><p> Tidak benar itu Pak Cah. Berarti selama ini \r\ndia tidak pernah tahu kalau saya marahi. Dia tidak pernah tahu kemarahan\r\n saya, jawab sang istri.</p><p> Jawaban ini spontan mengundang gelak \r\ntawa segar dari seluruh peserta yang jumlahnya sekitar seratus duapuluh \r\norang. Awalnya para peserta ingin mendapatkan tips dari pasangan suami \r\nistri ini, bagaimana hidup berumah tangga sudah lebih dari sepuluh tahun\r\n tetapi tidak pernah ada kemarahan di dalamnya. Namun ternyata yang \r\nterjadi tidaklah seperti itu. Kisah Boneka Rajut Kejadian tersebut \r\nmengingatkan saya dengan kisah lama yang juga pernah saya posting di \r\nKompasiana tentang boneka rajut.</p><p> Alkisah hiduplah sepasang kakek \r\ndan nenek di sebuah pinggiran kota nan sejuk. Mereka hidup bahagia dalam\r\n ikatan pernikahan, hampir 60 tahun mereka menjalani kebersamaan sebagai\r\n suami dan istri. Secara umum mereka tampak happy saja sebagaimana \r\nbanyak pasangan lainnya. Mereka berjanji saling mempercayai dan selalu \r\nsaling menjaga pasangannya. Sepanjang enampuluh tahun hidup bersama, \r\ntidak ada rahasia yang mereka sembunyikan, kecuali sebuah kotak yang \r\ndisimpan oleh nenek di atas lemari tua, di dalam kamar tidur mereka.</p><p>\r\n Ia selalu memperingatkan suaminya agar tidak menyentuh kotak itu sama \r\nsekali. Selama ini, suaminya tak pernah melanggar permintaan tersebut. \r\nHingga suatu hari, nenek yang sudah tua jatuh sakit dan harus dirawat di\r\n ruang intensif ICCU sebuah rumah sakit. Sang suami menyempatkan pulang \r\nke rumah untuk mengambil kotak itu dan membawanya ke sisi sang istri. \r\nKhawatir bahwa masa kematian istrinya sudah hampir tiba. Istrinya pun \r\nsetuju. Ia merasa ini memang sudah waktunya membuka kotak tersebut.</p><p>\r\n Ketika kotak dibuka, sang suami terkejut melihat isinya. Di dalam kotak\r\n terdapat sejumlah uang dalam jumlah yang banyak. Lembaran uang kertas \r\ntersebut diikat rapi dengan karet gelang. Ketika dihitung, jumlahnya \r\nmencapai US $ 95.000 atau lebih dari Rp 950 juta. Di dalamnya juga ada \r\ndua boneka rajut. .</p><p> "Dari mana ini semua, Nek?" tanya sang suami.</p><p> Panjang sekali ceritanya, sang istri mulai membuka cerita.</p><p>\r\n "Sejak awal menikah denganmu, nenekku menceritakan rahasia pernikahan \r\nbahagia. Menurutnya, rahasia itu adalah agar aku tidak bertengkar \r\ndenganmu, jadi aku harus mampu mengendalikan marah," ujarnya.</p><p> Awalnya tidak mudah bagiku mengendalikan amarah, lanjut sang istri.</p><p>\r\n "Hingga akhirnya nenekku menasehati, kalau aku sudah mulai marah \r\npadamu, sebaiknya aku merajut boneka," lanjut sang istri. Sang suami \r\nmendengar dengan seksama. Mendengar hal itu kini ia mulai mengerti, \r\nmengapa ada dua boneka rajut di dalam kotak itu. Ia juga merasa sangat \r\nterharu karena kesabaran sang istri. Hampir enampuluh tahun hidup \r\nbersama, istrinya hanya marah dua kali saja. Terbukti hanya ada dua \r\nboneka rajut di dalam kotak itu. Namun ia masih penasaran dengan uang \r\nsebanyak itu.</p><p> "Lalu dari mana uang sebanyak ini, Nek? Apakah \r\nengkau menabung dari uang belanja kita?" tanya sang suami. "Uang itu \r\nkudapatkan dari menjual boneka-boneka hasil jahitanku. Hasilnya selalu \r\naku simpan di kotak itu," jawab istrinya pelan.</p><p> Memahami Kemarahan Pasangan</p><p>\r\n Ternyata uang itu adalah hasil penjualan boneka-boneka yang dirajut \r\nsaat sedang menyalurkan kemarahan kepada suaminya. Entah sudah berapa \r\nboneka yang dibuat dan berapa banyak amarah yang berhasil dipendam sang \r\nistri. Kini sang suami baru mengerti, betapa selama ini sangat banyak \r\nsikap dan perlakuannya menyakiti hati sang istri.</p><p> Sedemikian \r\npandai sang istri menyalurkan kemarahan, sampai akhirnya suaminya tidak \r\nmengetahui bahwa sudah sangat banyak perbuatannya yang menyakiti hati \r\nsang istri. Tidak terhitung berapa banyak boneka rajut sudah berhasil \r\ndibuat dan dijual oleh sang istri selama ini. Menandakan sudah sangat \r\nbanyak kemarahan yang berhasil disalurkan secara positif oleh sang istri\r\n dan tidak diketahui oleh suami. Dua cerita di atas menandakan, \r\nkemarahan suami atau istri tidak selalu dimengerti oleh pasangannya. \r\nBisa jadi ada suami yang marah dan sakit hati terhadap istri, namun \r\nkarena pandai menyembunyikan atau menyalurkan dengan positif, sampai \r\nsang istri tidak mengetahui kalau suaminya marah.</p><p> Demikian pula \r\nbisa jadi ada istri yang marah dan sakit hati terhadap suami, namun \r\ntidak diketahui oleh sang suami. Di sini ada dua sisi yang harus \r\ndiperhatikan oleh pasangan suami istri. Pertama, jika sedang marah, \r\nhendaklah berusaha untuk menyalurkan secara positif dan konstruktif.</p><p>\r\n Jangan menyalurkan kemarahan dengan perkataan, perbuatan dan sikap yang\r\n negatif serta destruktif. Penyaluran negatif ini misalnya dengan \r\numpatan, teriakan, caci maki, sumpah serapah, kata-kata kotor, pukulan, \r\ntendangan, merusak barang-barang, dan lain sebagainya.</p><p> Cara \r\nmarah seperti ini memang sangat mudah dilakukan dan sangat mudah pula \r\ndimengerti oleh pasangan. Tidak perlu menjelaskan dengan kata-kata aku \r\nsedang marah, tapi kalau ekspresinya negatif seperti dijelaskan di \r\natas, dengan sangat mudah pasangan akan merasakan dan mengerti \r\nkemarahannya. Ekspresi kemarahan yang negatif dan destruktif ini tidak \r\nmenghasilkan kebaikan apapun baik bagi diri sendiri maupun bagi \r\npasangan. Terlebih ketika sudah memiliki anak, kemarahan seperti itu \r\nakan menjadi inspirasi bagi mereka.</p><p> Anda harus selalu belajar dan\r\n berusaha untuk menyalurkan kemarahan secara positif dan konstruktif. \r\nMisalnya ---bagi umat Islam--- dengan berwudhu, shalat sunnah, \r\nistighfar, dan kegiatan spiritual lainnya. Hendaknya selalu mengingat \r\nbahwa marah itu bukan ciri calon penghuni surga, sebagaimana sabda Nabi \r\nJangan marah bagimu surga. Atau disalurkan dengan melakukan tindakan \r\nyang produktif, seperti oleh raga, kerja keras, dan menyalurkan dengan \r\nkarya nyata.</p><p> Kisah boneka rajut di atas adalah salah satu contoh penyaluran marah dengan kerja dan karya nyata.</p><p>\r\n Kedua, hendaknya berusaha untuk memahami dan mengerti kemarahan \r\npasangan. Setiap orang memiliki ekspresi kemarahan yang khas, tidak \r\nselalu sama dengan orang lainnya.</p><p> Oleh karena itu, setiap kita harus berusaha mengenali ekspresi kemarahan pasangan, dan tingkat atau level kemarahannya. </p><p>\r\n Dengan mengenalinya, kita akan tahu betapa marahnya pasangan walaupun \r\nhanya diekspresikan lewat sikap diam dan mimik muka dingin. Saat \r\nmengetahui pasangan tengah marah, hendaknya anda berusaha mendekat dan \r\nmenenangkannya. <br> Sangat bagus jika sejak awal anda sudah memiliki \r\nkesepakatan dengan pasangan tentang sikap yang diharapkan pasangan saat\r\n ia marah.</p><p> Dengan itu anda akan tahu apa yang menyenangkan serta\r\n menenangkan hati pasangan saat ia tengah marah kepada anda. Ikut marah,\r\n membalas kemarahan dengan kemarahan, atau meninggalkan pasangan yang \r\ntengah marah, bukanlah tindakan yang tepat. Jika salah satu tengah \r\nmarah, yang lainnya harus berusaha cool dan tetap tenang. Jika tidak \r\nmengerti kemarahan pasangan, anda merasa ia baik-baik saja, padahal \r\ntengah menyimpan bom waktu yang siap meledak setiap saat. \r\nBerhati-hatilah.</p><p> .<br> Sumber : Cahyadi Takariawan, Konsultan Keluarga</p><br>', '["6"]', 'N', 'indonesia', 1452234381, 97),
(25, 10, 'resep-tradisional-pengobatan-cacar-air', 'Resep Tradisional Pengobatan Cacar Air', 'Pernahkah kamu mengalami diserang penyakit cacar setelah kamu dewasa? Sebagaimana kita ketahui cacar air merupakan penyakit yang \r\nmenyerang kulit. Penyakit ini disebabkan virus Herpes Varicella- Zoster.Penyakit ini juga merupakan penyakit menular dengan cara ...', 'Kesehatan,CacarAir', '<p><a href="http://sehatalamiku.com/wp-content/uploads/2015/02/obat-tradisional-cacar-air-vemale.jpg" target="_blank"></a><a href="http://sehatalamiku.com/wp-content/uploads/2015/02/obat-tradisional-cacar-air-vemale.jpg" target="_blank"><img src="http://sehatalamiku.com/wp-content/uploads/2015/02/obat-tradisional-cacar-air-vemale.jpg"></a></p><p>Pernahkah kamu mengalami diserang penyakit cacar setelah kamu dewasa? Sebagaimana kita ketahui cacar air merupakan penyakit yang \r\nmenyerang kulit. Penyakit ini disebabkan virus Herpes Varicella- Zoster.</p><p>Penyakit ini juga merupakan penyakit menular dengan cara penularan \r\nmelalui kontak fisik maupun secara tidak langsung. Penyakit ini bukanlah\r\n penyakit berbahaya bagi orang biasa, namun menjadi mematikan bagi \r\npenderita kanker.</p><p>Hal ini disebabkan, melemahnya daya tahan tubuh \r\npenderita kanker yang tidak mampu melawan virus ini. Saat ini, penyakit \r\ncacar mudah disembuhkan dengan banyaknya obat anti biotik. Walaupun \r\ndemikian, tidak ada salahnya jika Anda mencoba obat tradisional untuk mengobati penyakit ini.</p><p>Pengobatan tradisoinal cacar air bisa dari dalam dengan cara membuat ramuan yang diminum langsung atau bisa juga melalui pengobatan dari luar yaitu dengan cara mengoles kebagian tubuh yang sakit. Pengobatan dari dalam bertujuan untuk mematikan \r\nvirus yang berkembang sedangkan pengobatan luar ditujukan untuk \r\nmenghilangkan noda bekas cacar air. Berikut ini akan coba dibagikan \r\nresep ramuan <strong>obat tradisional cacar air</strong> untuk pengobatan dalam, yaitu :</p><p>Bahan ramuan obat tradisional cacar air </p><p>Resep pertama, bahan-bahan yang diperlukan adalah :</p><ul>\r\n<li>30 gr temulawak</li><li>25 gram kencur</li><li>15 gram asam jawa</li><li>15 gram jahe dipotong-potong</li><li>600 cc air</li></ul><p>Cara mengolahnya adalah</p><ul>\r\n<li>Rebus semua bahan yang telah tersedia hingga mendidih</li><li>Lalu biarkan hingga air rebusan tersisa 300 cc</li><li>Saring air rebusan dan minum sebanyak 2  3 kali sehari</li></ul><p>Resep kedua adalah dengan memanfaatkan \r\nbuah mengkudu yang dibuat jus atau bisa juga diparut dan diambilnya \r\nairnya kemudian diminumkan kepada penderita.</p><p>Jika penyakit cacar air telah sembuh, \r\nmaka yang membuat pasien suka kesal adalah kenyataan memiliki noda yang \r\nsusah untuk dihilangkan. Untuk itu diperlukan pengobatan luar guna \r\nmenghilangkan noda jerawat ini. Berikut ini ada 2 resep untuk <strong>obat tradisional cacar air</strong> dari luar yaitu :</p><p>Resep kedua, bahan bahan yang \r\ndibutuhkan adalah:</p><p>1.  kunyit </p><p>2. asam jawa </p><p>Cara mengolahnya adalah</p><p>Tumbuk halus kedua bahan tersebut di ata, kemudian mencampurkan bahan yang tersedia dengan \r\nminyak kelapa dan kemudian dipanaskan sebentar. </p><p>Jika sudah dingin, baru \r\ndioleskan ke seluruh tubuh, terutama pada bagian yang terkena cacar air. </p><p>Resep ketiga, bahan-bahan yang diperlukan adalah</p><p>1. daun asam </p><p>2.  kunyit</p><p>Cara membuatnya dengan mencuci bersih semua bahan dan dihaluskan lalu \r\ndioleskan ke kulit yang terkena cacar saja.</p><p>Selain ketiga resep tersebut, ada juga \r\nresep yang lain yaitu dengan memanfaatkan daun ciplukan, yaitu dengan  mandi 2 \r\nkali sehari secara rutin dengan air rebusan daun ciplukan ini, kulit \r\nAnda akan mulus kembali seperti semula.<strong></strong></p><p><strong></strong>Demikian beberapa resep cara mengobati cacar/cacar air secara tradisional,  semoga bermanfaat dan dapat membantu Anda dalam mengobati dan \r\nmenyembuhkan penyakit cacar air. </p><p>Selain itu juga mampu menghilangkan \r\nnoda yang membandel yang tidak mau hilang. Semoga dengan obat ini, \r\npenyakit cacar air yang Anda derita mampu sembuh dan tanpa meninggalkan \r\nnoda. Selamat mencoba..</p>', '["12"]', 'Y', 'indonesia', 1452702937, 64),
(26, 10, 'belajar-menghormati-setiap-orang', 'Belajar Menghormati Setiap Orang', 'Menjadi orang yang tidak sombong, tinggi hati dan merasa lebih dari orang lain bagi sebagian orang mungkin susah, namun cobalah tengok kisah berikut ini; Suatu pagi, seorang wanita \r\nberpenampilan menarik berusia 40-an membawa anaknya memasuki area \r\nperkantor...', 'Cerita', '<p><a href="http://www.dwitri.com/uploads/posts/12b250be11e01d4c0622a89cbd4b399c.jpg" target="_blank"></a><a href="http://www.dwitri.com/uploads/posts/12b250be11e01d4c0622a89cbd4b399c.jpg" target="_blank"><img src="http://www.dwitri.com/uploads/posts/12b250be11e01d4c0622a89cbd4b399c.jpg"></a></p><p>Menjadi orang yang tidak sombong, tinggi hati dan merasa lebih dari orang lain bagi sebagian orang mungkin susah, namun cobalah tengok kisah berikut ini;</p><p> Suatu pagi, seorang wanita \r\nberpenampilan menarik berusia 40-an membawa anaknya memasuki area \r\nperkantoran sebuah perusahaan terkenal. Karena masih sepi, mereka duduk \r\ndi taman di samping gedung kantor tersebut sambil menikmati cemilan, \r\nseraya menikmati hamparan hijau nan asri.</p><p> Selesai makan, dengan \r\nsantai si wanita membuang sembarangan tisu bekas pakai. Tidak jauh dari \r\nsitu, ada seorang kakek tua berpakaian sederhana memegang gunting untuk \r\nmemotong ranting. Dengan menatap tajam, kakek itu menghampiri, memungut sampah tisu dan membuangnya ke tempat sampah.</p><p>\r\n Beberapa waktu kemudian, kembali wanita itu membuang bekas makanan \r\ndengan sembarangan. Lagi-lagi kakek itu dengan sabar memungut dan \r\nmembuangnya ke tempat sampah.</p><p> Sambil menunjuk ke arah sang kakek,\r\n si wanita itu lantang berkata ke anaknya, "Nak, kamu lihat di \r\nbelakangmu, jika tidak sekolah dengan benar, nanti masa depan kamu cuma \r\nseperti kakek itu. Kerjanya mungutin dan buang sampah! Jelas, ya?"</p><p> Si kakek meletakkan gunting dan menyapa wanita itu, "Permisi, ini adalah taman pribadi, bagaimana Anda bisa masuk ke sini?"</p><p> Wanita itu dengan sombong menjawab, "Saya? Oh, saya adalah calon manager logistik yang dipanggil oleh perusahaan ini."</p><p>\r\n Di waktu yang bersamaan, seorang pria dengan sikap sopan dan hormat \r\nmenghampiri kakek tua tersebut sambil berkata, "Maaf Pak Presiden Direktur, hanya mau mengingatkan saja, rapat sebentar lagi akan segera dimulai pak..."</p><p> \r\nSang kakek mengangguk. Lalu sambil mengarahkan matanya ke wanita itu, dia berkata tegas, "Manager, tolong untuk wanita ini, saya tidak \r\ncocok untuk mengisi posisi apa pun diperusahaan ini."</p><p> Sambil melirik ke arah si wanita, si manager menjawab cepat, "Siap Pak Presdir, sesuai perintah Bapak."</p><p>\r\n Setelah itu, sambil berjongkok, sang kakek mengulurkan tangan sambil \r\nmembelai kepala si anak yang sejak tadi memperhatikannya, lalu berkata: \r\n"Nak, di dunia ini, yang penting adalah belajar untuk menghormati setiap\r\n orang, siapa pun dia, entah direktur atau tukang sampah sekalipun!"</p><p>Semoga dengan membaca kisah tersebut di atas kita dapat memetik satu pelajaran berharga, yaitu tentang bagaimana caranya menghormati orang lain. Karena yang kita lihat dan ketahui belum tentu seperti kenyataannya jadi lebih baik </p><p> berpikir berkali-kali jika ingin sombong. Karena hasilnya pasti malu dan zonk... :D</p><p>referensi dan inspirasi : MP</p>', '["10"]', 'N', 'indonesia', 1452432703, 48),
(27, 10, 'menulis-di-atas-air', 'Menulis Di Atas Air', 'Mengutarakan pendapat di hadapan orang yang membencimu, ibarat menulis di atas air.....\r\n Ketika kau menulis di permukaan air maka riak air itu akan menghambat \r\nlaju penamu dan ketika kau mencoba menulis kembali maka yang kau hadapi \r\nair itu bukannya tenang ...', 'Fiksi', '<p><a href="http://www.dwitri.com/uploads/posts/df2ef189dcdc1ed93eec27c4e4a787b7.jpg" target="_blank"><img src="http://www.dwitri.com/uploads/posts/df2ef189dcdc1ed93eec27c4e4a787b7.jpg"></a></p><p>Mengutarakan pendapat di hadapan orang yang membencimu, ibarat menulis di atas air.....</p><p><br>\r\n Ketika kau menulis di permukaan air maka riak air itu akan menghambat \r\nlaju penamu dan ketika kau mencoba menulis kembali maka yang kau hadapi \r\nair itu bukannya tenang malah semakin bergelombang...</p>', '["10"]', 'N', 'indonesia', 1452528544, 37),
(28, 10, 'entah-kenapa-mendadak-megawati-sulit-menyebut-nama-allah-swt-di-rakernas', 'Entah Kenapa, Mendadak Megawati Sulit Menyebut Nama Allah SWT di Rakernas', 'Kenapa Lidah Mega Kelu Saat Ucapkan Allah SWT dan Salam..Entah kenapa dengan Ketua Umum PDI Perjuangan, Megawati Soekarnoputri di\r\npenghujung pidatonya dalam rakernas I partai di JIExpo Kemayoran, Jakarta,\r\nMinggu (10/1). Lidah Megawati mendadak kelu saat meny...', 'megawati', '<p><img src="http://www.dwitri.com/uploads/posts/896e7d65f7f7e2f8e2d1f99a74598d50.jpg"></p><p><br><strong></strong></p><p><strong><br></strong></p><p>Kenapa Lidah Mega Kelu Saat Ucapkan Allah SWT dan Salam..</p><p>Entah kenapa dengan Ketua Umum PDI Perjuangan, Megawati Soekarnoputri di\r\npenghujung pidatonya dalam rakernas I partai di JIExpo Kemayoran, Jakarta,\r\nMinggu (10/1). Lidah Megawati mendadak kelu saat menyebut nama Allah SWT dan\r\nsalam.</p><p>Semoga Allah su Allah suwan.. Allah Subhana wa Taala meridhoi perjuangan\r\nkita, tanamkan dalam hati jiwa dan pikiranmu. One for All all for one. Assalamualaikum\r\nwarah matulallahi wabarokatih (seharusnya wabarakatuh), kata Megawati sambil\r\ntersengguk.</p><p>Sebelum menutup pidatonya, Megawati tampak terlihat berkaca-kaca.</p><p>Padahal sebelumnya Megawati telah menyemangati kader-kadernya untuk tidak\r\ntakut dalam memperjuangkan sebuah kebenaran. Dia pun memberikan selamat bagi\r\npelaksanaan rakernas.</p><p>Di dalam masyarakat Indoensia itu kita akan hidup bahagia sesuai cita-cita\r\norangtua kita di zaman dahulu. Selamat berpikir bekerja dan berjuang, jangan\r\nragu bersuara, selama argumentasinya sesuai dengan konstitusi jangan setengah\r\nperjuangan. Jangan takut selama yang kita perjuangkan kebenaran. Hakkul yakin,\r\nannul yakin, terangnya.</p><p>Rakernas I PDIP akhirnya dibuka dengan pemukulan gong yang dilakukan\r\nMegawati dan pemotongan tumpeng.</p><p>Kembali ke judul di atas kenapa ya, aneh.</p>', '["4"]', 'N', 'indonesia', 1452528874, 32);
